css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kaixin001.com"),
               domain("top100.cn"),
               domain("timeanddate.com"),
               domain("weather.com.cn"),
               domain("news.google.cn"),
               domain("www.bullock.cn")
 {

@charset "utf-8";
/*    Name: 1490680844
    High contrast (W/B)
    Copyright 2007 Opera Software */
* {
    color: lime !important;
    background: black !important;
}
input, textarea, select {
    color: black !important;    
    background: lime !important;
}
button, input[type="file"], input[type="submit"], input[type="button"], input[type="reset"] {
    color: black !important;    
    background: silver !important;
}
a[href] {text-decoration: underline !important;
         color: yellow !important;    
    background: black !important;
}
a[href]:hover {
    color: black !important;    
    background: yellow !important;
}
a[href] img {border: thin solid lime !important;}
@media print {
    * {
        color: black !important;    
        background: lime !important;
    }
}



}
