css:
@-moz-document domain('4channel.org'),domain('4chan.org')
{

    .mobile, #blotter, .middlead, #toggleMsgBtn, .globalMessage, .adc-resp-bg, hr{
        display: none;
    }

    .boardBanner{
        padding: 20px 0;
        display: flex;
        flex-direction: row-reverse;
        justify-content: center;
        align-items: center;
        column-gap: 10vw;
    }

    #bannerCnt{
        margin: 0;
        padding: 0;
    }

    #togglePostFormLink{
        font-size: 10pt;
        font-weight: normal;
    }


    #ctrl-top hr, #ctrl-top + hr, .board hr, .adc-resp-bg + hr, #ctrl + hr, .navLinks + hr{
        display: block;
    }

}
