css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rabr.in") {

h2,#logo{
display: none !important;
}

#tip b{
font-size: 9px !important;
}
#tip {
position: fixed !important;
left:15px !important;
top:15px !important;
}

/*main part*/
#statuses{
position: fixed !important;
left:0px !important;
top:20px !important;
height: 93% !important;
width: 95% !important;
display:block!important;
overflow:auto!important;
-moz-column-width:300px !important;
-moz-column-gap: 2em !important;
-moz-column-rule: 1px solid gold !important;
}

.thumb_pic img{
max-width:300px!important;
}
.status_author{
width:30px!important;
height:30px!important;
}
.status_author img{
width:30px!important;
height:30px!important;
}
.status_body{
position: relative!important;
left:-20px !important;
top:0px !important;
width:95%!important;
height:92%!important;
line-height:1.2em!important;
}
li{
height:85%!important;
}
.status_info {
position: relative!important;
left:10px !important;
top:-5px !important;
padding-bottom:0px !important;
}

.status_info * {

height: 9px!important;
margin:0px!important;
font-size: 9px !important;
}
#statuses li:nth-of-type(odd){
-moz-border-radius:0px 10px 0px 10px  !important;
background:#D8DFEA!important;
}

#statuses ol{

}

/*function buttons*/
#func_set{
position: relative!important;
left:0px !important;
top:0px !important;

}

#photoArea,#filterArea{
width:350px!important;
height:80px!important;
position: absolute !important;
left:0px !important;
top:0px !important;
z-index:999 !important;
}


/*last msg*/
#currently{

}



/*update button*/
#submit_btn{
display:none  !important;
}

/*side bar*/
#side_base {
position: fixed !important;
right:0px !important;
top:0px !important;

}

#side_base{
background:-moz-rgba(255,255,255,0.6)!important;
}
#side_base{
opacity:1!important;
}


/*footer*/
#footer {
position: fixed !important;
right:0px !important;
bottom:0px !important;
}
#footer *,#nav *{
font-size:9px  !important;
}

/*header bar*/
#nav {
position: fixed !important;
left:-10px !important;
top:0px !important;
}

#textbox {
width:300px !important;
height:50px !important;
}

.content-bubble-arrow{
display:none!important;
}
.allBtn{
width:85px !important;
overflow:hidden !important;
padding:0px!important;
}
}
