css:
@-moz-document domain("utweb.trontv.com"), domain("127.0.0.1") {
    /* Main */

    /* MAKE NAVBAR SMALLER */
    #navbar {
        height: 45px !important;
    }
    
    /* REMOVE THE SAFE TORRENT ICON AND SEARCH FROM NAVBAR */
    .safe-torrent-icon, .search-control-nav-main {
        display: none !important;
    }
    
    /* REMOVE SIDEBAR ADS */
    .ad-free-upsell-container {
        display: none !important;
    }
    
    #sidebar > div.card-container {
        display: none !important;
    }
    
    /* REMOVE SPEED AND TRENDING VIDEOS MENU ITEMS */
    #sidebar > div.sidebar-content > div.sidebar-item-speed {
        display: none !important;
    }
    
    #sidebar > div.sidebar-content > div:nth-child(4) {
        display: none !important;
    }
    
    /* UPDATE THE SIDEBAR STYLING */
    #sidebar, .sidebar-content {
        background-color: transparent !important;
    }
    
    .sidebar-content {
        flex: initial !important;
        flex-direction: initial !important;
        float: left !important;
    }
    
    /* MOVE SIDEBAR ITEMS TO THE NAVBAR */
    #sidebar[data-v-3b484781] {
        position: absolute !important;
        top: -50px !important;
        left: 250px !important;
    }
    
    /* REMOVE THE OVERFLOW SO THE SIDEBAR ITEMS ARE VISIBLE */
    .content,
    .view-container {
        overflow: initial !important;
    }
    
    /* UPDATE SIDEBAR ITEMS STYLING */
    .sidebar-item .sidebar-link {
        padding-right: 8px;
    }
    
    .sidebar-icon {
        padding: 0 8px !important;
        width: 100% !important;
    }
}
    
@-moz-document regexp(".*utweb.trontv.com/.*/library"), regexp(".*127.0.0.1/.*/library") {
    /* Library page */
    
    /* REMOVE THE TOP AD */
    .card-container[data-v-0d5fc99e] {
        display: none !important;
    }
    
    /* REMOVE THE TOP MARGIN FROM THE TORRENT CONTROLS AFTER AD REMOVAL  */
    #media-library .media-library-container .media-library-top-container.show-ad[data-v-0d5fc99e] {
        top: 0 !important;
    }
}
    
@-moz-document regexp(".*utweb.trontv.com/.*/dashboard"), regexp(".*127.0.0.1/.*/dashboard") {
    /* Dashboard page */

    /* REMOVE THE TOP AD */
    .card-container[data-v-720a42b0] {
        display: none !important;
    }
    
    /* FIX THE TOP MARGIN AFTER AD REMOVAL */
    .dashboard-container .dashboard-inner-container[data-v-720a42b0] {
        padding-top: 14px;
    }
    
    /* REMOVE THE CONTENT PLAYER */
    .content-player-container {
        display: none !important;
    }
}
