css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.teamliquid.net") {

/* (uncomment the following line to hide header) */
/*  #userpanel,
    .topmenubar,
    #tlverytopbanner,  */
  
td.titelbalk>div, td.titelbalk>img, td.titelhigh>div, td.titelhigh>img,
table[width="994"]>tbody>tr+tr,

td[style="border-top: 1px solid rgb(0, 0, 93);"],
td[style="border-top: 1px solid #00005D;"],
td[style="border: 1px solid rgb(0, 0, 93); text-align: center;"],
.grijs>table>tbody>tr>td[align="center"],
.spr, .spr2, .spacer,
a[href="http://www.iccup.com/"],
a[href="http://www.liquidpoker.net"],
a[href="http://www.thelittleappfactory.com/"]
{display:none !important;}

* {background-color: #2c2c2c !important; color: #eee !important;}

td[width="994"]{background:none !important; height:50px!important;}
body {margin-bottom:20px !important;}
td[width="180"]{padding-top:37px!important;}

.lichtb, .forumPost{background-color: #373737 !important;}
.titelbalk, .titelbalk *, .titelhigh, .titelhigh * {background-color: #555 !important;}

*:not(table):not(.lichtb), #calendar {border:none !important;}
table.solid, td.lichtb {border-color: #555 !important;}
td {background-image:none !important;}
td[rowspan="2"]{color: #2c2c2c !important;}
[title="Bold"], [title="Italic"], [title="Underline"] {background-color: #d9dde0 !important;}

.quote {margin-left: 30px !important; padding-left: 15px !important;}
.forummsginfo>span, .forummsginfoa>span {font-weight:normal!important;}
.forummsginfo>img, .forummsginfoa>img {display:none!important;}

a{text-decoration:none !important; color:#d5d5d5!important;}
.forumPost a { text-decoration:underline!important;}
a:hover{color: #79c !important;}

h1.title {border: 2px solid #666 !important; -moz-border-radius: 8px;}
h1.title>img {background-color: transparent !important;}
.highlight, .cont, #news_articles>h3 {background-image:none !important;}
#news_spotlight>h3, #news_community>h3>a,  #news_sponsored h3>a,
#news_store>h3>a, #news_results>h3>a, #news_poll>h3>a, #news_articles>h3>a
  {background:none!important; border: 1px solid #555 !important; -moz-border-radius: 5px;}

/* (firefox users may want to uncomment the following, to align text at bottom left) */
/*
td[width="180"] {text-align:left !important; text-indent:10px;}
td[width="180"]>a {margin-left:10px;}
td[width="180"]:not([rowspan="2"])>a {margin-left:17px;}
*/

/*BW VODs*/
.tp, .bt,
img[src="/mirror/layout/bottom_wide.png"]
{display:none!important;}

.md, .md input {background-image:none!important;}

}
