css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockpapershotgun.com") {

div#main {
width: auto !important;
margin:0 10px  !important;
}
div#container {
width: auto !important;}
div#left-column{
width:70% !important;
}
div#right-column {
width:29% !important;}
form#commentform{
margin: 0 auto; !important
}
div#right-column div:first-child{
width: 100% !important;
}
.navigations{
width:auto !important;
}

div#right-column div.block img{
max-width:278px !important;
width:100% !important
}
div#left-column div.block img{
margin: 0 auto !important;
display:block !important;
width:100% !important;
max-width:600px !important;
}

div#left-column div.block img[src*=tosser] {
width: 50px !important;
border: 1px solid #aaa !important;
margin-right: 10px !important;
margin-top: 6px !important;
}


body.page-id-9 div#left-column div.block img {
max-width:250px !important;
}

div#right-column form {
width:auto !important;
}
input#search-site{
margin-right:10px !important;
}
form fieldset input.submit{
margin-left: 0 !important;
}
a.marketing img{
width:100% !important;
max-width:300px !important;
}

div#footer-content{
width:auto !important;
}
div#footer-content div.footer-col{
margin-right:3% !important;
width:30% !important;
}
.col3 ul {
width: auto !important;
}

#slider {
width: 100% !important;
border: 0 !important;
margin-bottom: 26px !important;
}
.scrollContainer div.panel {
width:100% !important;
background-repeat: no-repeat !important;
background-color: #000 !important;
}
.scrollContainer div.panel div{
position:absolute !important;
}

ol.commentlist p{
margin-left: 80px !important;
}
div#left-column div.block img.avatar{
position:absolute !important;
width:64px !important;}
ol.commentlist .comment-author cite {
margin-left: 80px !important;
}


body {
font-family: Trebuchet MS !important;
}
ol.commentlist .comment-meta{
margin-top:10px !important;}
div#left-column div.block p{
line-height:1.7 !important;
}
ol.commentlist li.comment-author-rossignol,ol.commentlist li.comment-author-botherer,ol.commentlist li.comment-author-ento,ol.commentlist li.comment-author-kierongillen {
border: 1px solid #eba !important;
background-color:#fdd !important;
}
ol.commentlist blockquote{
background-color: #f6f6f6 !important;
border: 0 !important;
-moz-border-radius:5px !important;
padding:0 0.5em !important;
margin-left:80px !important;
margin-bottom: 1em !important;
}
ol.commentlist blockquote p {
margin-left: 0 !important;
}
}
