css:
@-moz-document domain("archive.org") {
  #file-dropper-img {
    background-color: white;
    max-height: 100px;
    border-radius: 12px;
  }
  
  body.ia-module .welcome.account-uniform {
    background-color: #ffffff;
    color: #2c2c2c;
  }
  
  body.ia-module .welcome.account-uniform .tabby.in a.stealth {
    color: #2c2c2c;
    border-bottom: 2px solid #2c2c2c;
  }
  
  body.ia-module .welcome.account-uniform .tabby a.stealth {
    color: #878787;
    transition: .3s;
  }
  
  body.ia-module .welcome.account-uniform .tabby a.stealth:hover {
    color: #404040;
  }

  body.ia-module.tiles .item-ia {
    border-radius: 8px;
  }

  body.ia-module.tiles .item-ia .item-img {
    border-radius: 8px 8px 0 0;
  }

  body.ia-module .welcome .row h1 {
    font-weight: bold;
    margin-top: 0;
  }

  .account-description {
    font-weight: 400;
  }

  body.ia-module.tiles .item-ia .mt-icon {
    margin: 6px 6px 6px 0;
  }

  body.ia-module .welcome.account-uniform .stealth {
    color: #2c2c2c;
  }
  
  body.ia-module.tiles .item-ia .collection-stats {
    transition: .3s;
  }
  
  body.ia-module.tiles .item-ia.collection-ia {
    transition: .3s;
  }
  
  .textarea-invert-readonly {
    background-color: #040404 !important;
    transition: .3s background-color;
  }
  
  .textarea-invert-readonly:hover {
    background-color: #121212 !important;
  }
  
  body.ia-module .welcome .account-description a {
    color: #4b64ff;
    text-decoration: none;
  }
 
  body.ia-module .welcome .account-description a:hover {
    color: #4b64ff;
    text-decoration: underline;
  }
  
  input[type="text"] {
    border: 1px solid #CCCCCC;
    padding: 3px 9px;
    color: black;
    border-radius: 3px;
  }

  input[readonly] {
    color: #767676;
    border: 1px solid #CCCCCC;
    padding: 3px 9px;
    border-radius: 3px;
  }
  
  textarea[readonly] {
    color: #767676;
    border: 1px solid #CCCCCC;
    padding: 5px 11px;
    border-radius: 3px;
  }

  .wysiwyg {
    border-radius: 7px;
  }
  
  div.wysiwyg iframe {
    padding: 10px;
  }
  
  textarea { 
    color: black;
    border: 1px solid #CCCCCC;
    padding: 3px 9px;
    box-shadow: inset 0 1px 1px #00000013;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
  }
  
  input[type="text"] {
    color: black;
    border: 1px solid #CCCCCC;
    padding: 3px 9px;
    box-shadow: inset 0 1px 1px #00000013;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
  }
  
  textarea {
    border-radius: 4px;
  }
  
  input[type="text"]:focus, textarea:focus {
    border-color: #66afe9;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px #66afe999, 0 0 8px #66afe999;
    box-shadow: inset 0 1px 1px #00000013, 0 0 8px #66afe999;
  }
  
  input {
    color: black;
    border-radius: 3px;
    border: 1px solid #CCCCCC;
    padding: 3px 9px;
    box-shadow: inset 0 1px 1px #00000013;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
  }


  input:focus {
    border-color: #66afe9;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px #66afe999, 0 0 8px #66afe999;
    box-shadow: inset 0 1px 1px #00000013, 0 0 8px #66afe999;
  }
  
  input[type="text"].roundbox20 {
    border-radius: 20px;
  }
  
  input[type="radio"], input[type="checkbox"]:focus {
    box-shadow: 0 0 0 black;
  }
  
  input.rbt-input-main.form-control.rbt-input {
    padding: 3px 12px;
  }
  
  #related-items>div {
    border-radius: 10px;
  }

  fieldset {
    box-shadow: rgb(195 173 151) 0 0 0px 0px;
  }
}
