css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.deviantart.com/") {

.tt-a{padding:40px!important; padding-bottom:60px!important;}

.browse2-results{background: #1a1a1a none!important;border: 0 solid transparent!important;}

#heyboxContents, #heyboxShadow, #heybox, #facetMenu-Category{background: #1d1d1d none!important;border: 0 solid transparent!important;color:#515151!important;}

#browseNavbar, #facetMenu-Browse, .f{background: #1f1f1f none!important; border: 0 solid transparent!important;}


.sliding-menu ul *, #facetMenu-Browse_list li a{color: #94ae13!important;}
.sliding-menu ul *:hover,  #facetMenu-Browse_list li a:hover{color: #1f1f1f!important;}
#facetMenu-Browse_list li a.selected{color: #94ae13!important;}
.noncollapseHeader{color:#616161!important; text-shadow:0 0 0 #616161!important;background:#2f2f2f none!important;}

.tabInactive, a.a, a.t{color:#94ae13!important; text-shadow: 0 0 0 transparent!important;}
.tabInactive:hover, a.a:hover, a.t:hover{color:#1f1f1f!important;background:#94ae13!important;}
.tabActive, a.selected{color:#acca1c!important; text-shadow: 0 0 0 transparent!important;}
.tabActive:hover, a.selected:hover{color:#acca1c!important; background:transparent none!important;}

small a:hover{color:#1a1a1a!important; background:#3B5A4A none!important; text-decoration:none!important;}

#catBar{background: #222 none!important;border-color:#252525!important;}
.catTitle{color: #515151!important;}
.catTitle a{color:#94ae13!important;}

.shadow img{border:5px solid #101010!important; -moz-border-radius:5px!important;}
.shadow img:hover{border-color: #acca1c!important;}

#output{background-color:#252525!important;}
#top{background:#202020 none!important;}

.ch.bubbleprefix{display:none!important;}

#moremenu li {background: transparent url(data:image/png;base64,)!important;top:-4px!important;}
#moremenu li a, #moremenu li a i{background: #202020 none!important; color:#94ae13!important;}
#moremenu li a:hover{color:#202020!important; background: #94ae13!important;}
i.i21{background:#202020 none!important; color:#515151!important;}

#depths{background: #141414 none!important;}

#mcstatus{background: #141414 none!important;}

.pagescroll .f{background:#c1d1bc none!important;}


.mcb-midview .tt-a{padding:5px!important;}
.gallery-stream-row{height: 300px!important;}
img.lit{border:0 none transparent!important;}

}
