css:
@-moz-document domain(kulino.dinus.ac.id), url-prefix('https://kulino.dinus.ac.id') {
body {
  padding-top: 0px;
  background: url(https://lyrihkaesa.github.io/wallpaper/1.jpg) no-repeat fixed 0 0;
  background-size: cover;
  line-height: 20px;
}

#page-course-view-weeks {
    background: url(https://lyrihkaesa.github.io/wallpaper/2.png) no-repeat fixed 0 0;
    background-size: cover;
}

#page-mod-forum-view {
  background: url(https://lyrihkaesa.github.io/wallpaper/3.jpg) no-repeat fixed 0 0;
  background-size: cover;
}

#wrapper {
  border-top: 0px;
}

.container-fluid.socials-header {
  display: none;
}

.logo-header {
  display: none;
}

#loggedin-user > a > img {
  display: none;
}

#page-header {
  font-size: 10px;
  padding: 0px;
  background: none;
}

.navbar {
  display: none;
}

#page {
  padding-top: 0px;
  background: none;
}

#page-footer {
  background: none;
  border-top: none;
}

.btn {
  border-radius: 5px;
  padding: 3px 9px;
  font-size: 12px;
}

#block-region-content > section > div > div > div.block-myoverview.block-cards > div.d-flex.align-items-center.flex-wrap {
  display: none!important
}

.card-img.dashboard-card-img {
  height: 16px;
}

.ml-auto.dropdown {
  display: none;
}

.blockstyle-01 #region-main .block .card-title {
  padding: 3px!important;
  border-radius: 5px;
}

.blockstyle-01 #region-main .block .card-title::before {
  font-size: 10px;
  padding: 2px;
  margin: 0px 10px 0px 0px;
  line-height: 20px;
}

#paged-content-container-1 > div.mt-1 > div {
  display: none;
}

#region-main .block .content {
  margin: 0px;
  padding: 0px;
}

@media(min-width: 647px) {
   .dashboard-card-deck .dashboard-card {
      width: calc(50% - 1rem);
    }
}

@media (min-width: 888px){
.dashboard-card-deck .dashboard-card {
    width: calc(33.333% - 1rem);
}
}

@media (min-width: 1147px){
.dashboard-card-deck .dashboard-card {
    width: calc(25% - 1rem);
}
}

.card-body {
  background: rgba(255,255,255,0.9);
   border-radius: 9px;
   
}

.card {
  background: rgba(255,255,255,0.9);
}

#block-region-content > section > div.card-body.p-3{
  background: none;
}

.card.dashboard-card {
  border-radius: 9px;
  overflow: hidden;
  border: none;
  margin-bottom: 0.5rem;
}

.card.dashboard-card > .card-body{
  background: none;
}

#page-navbar > div > nav {
  background: rgba(255,255,255,0.9);
  padding: 2px 5px 2px 5px;
  border-radius: 4px;
}

.footerlinks {
  background: none;
}

.tab-content {
  background: rgba(255,255,255,0.9);
  border-radius: 9px;
}

.nav {
  margin-bottom: 4px;
}

.nav-tabs {
  border-bottom: none;
}

.nav-tabs>li>a {
  border-radius: 9px;
}

.course-pano.wrapper {
  height: 40px;
  background: none;
  box-shadow: none;
}
.course-pano.title {
  height: none;
  padding: 0px;
}
.course-pano>img {
  display: none;
}

.course-pano.title h2 {
  margin: 0px;
}

.course-pano.title h2 span {
  border-radius: 9px;
}

.course-pano.title h4 {
  margin: 0px;
}

.label .mod-indent {
  padding-top: 0px;
}

.course-pano>img {
  display: none;
}

.table {
  background: rgba(255,255,255,0.9);
  border-radius: 9px;
  margin-bottom: 0px;
  overflow: hidden;
}

#beranda > table {
  background: none;
}

.modtype_resource {
  background: rgba(255,255,255,0.9);
  border-radius: 5px;
  overflow: hidden;
}

.course-content .section .activity:hover {
  background: rgba(255,255,255,0.7);
}

.modtype_attendance {
  background: rgba(255,255,255,0.9);
  border-radius: 5px;
  overflow: hidden;
}

.modtype_forum {
  background: rgba(255,255,255,0.9);
  border-radius: 5px;
  overflow: hidden;
}

.modtype_page {
  background: rgba(255,255,255,0.9);
  border-radius: 5px;
  overflow: hidden;
}

.modtype_assign {
  background: rgba(255,255,255,0.9);
  border-radius: 5px;
  overflow: hidden;
}

.modtype_url {
  background: rgba(255,255,255,0.9);
  border-radius: 5px;
  overflow: hidden;
}

.course-content .current {
  background-color: rgba(	167,243,208, 0.9)!important;
  padding: 10px 0px 10px 0px;
  border-radius: 15px;
}

#region-main {
  background: rgba(255,255,255,0.9);
  padding-left: 10px;
  padding-right: 10px;
  border-radius: 10px;
}

#page-footer .row-fluid .span4 .block {
  padding-top: 0px;
}

#block-region-content > section {
  margin-bottom: 0px!important;
}

#block-region-content > section > div > div {
  min-height: 0px;
}

}
