css:
@-moz-document domain("discord.com")
{
/* ------BACKGROUND------ */
  body
  {
    background: url(https://i.slow.pics/Vx4lH6eI.png);
    background-size: cover;
  }
/*[[theme]]*/

/*remove borders*/
  .header-2o-2hj,
.searchBar-6Kv8R2,
.content-yTz4x3::before
  {
    box-shadow: none !important;
  }

/*misc*/
  .container-1D34oG,  /*friends list*/
.scrollbar-2rkZSL,  /*scrollbar*/
.pad-29zQak,
.track-1JN30G
  {
    background: transparent !important;
  }

/* ------SCROLLBAR------ */
  .scrollbar-2rkZSL
  {
    width: 8px;
  }

  .thumb-2JwNFC
  {
    background: rgba(0, 0, 0, .5) !important;
  }

  .track-1JN30G,
.thumb-2JwNFC
  {
    border: none !important;
  }

/* ------EMBEDDED TEXT------ */
  .embedProvider-3k5pfl,
.embedDescription-1Cuq9a,
.embedFields-2IPs5Z,
.embedAuthorName-3mnTWj,
.embedFooterText-28V_Wb
  {
    color: #f2f2f2 !important;
  }
}
