css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(boards.4chan.org), domain(boards.4channel.org)  {
.yotsuba-b:root .field:focus {
    border-color: #B6D96F!important;
}
input[type="text"]:focus, input[type="password"]:focus, input:focus:not([type]), textarea:focus {
    border: 1px solid #B6D96F !important;
}
.yotsuba-b:root .qr-link
{
	background: linear-gradient(#F4FBE7, #D7EED1) repeat scroll 0 0 rgba(0, 0, 0, 0)!important;
	border-color: #D4E1C7 #D4E1C7 #C1D2B8!important;
}
.yotsuba-b:root .qr-link:hover
{
	background: none repeat scroll 0 0 #EAF4DA!important;
}
.inline, .yotsuba-b:root .dialog
{
	border-color:#bfd9b7!important;
}
.dialog
{
	background-color:#daf0d6!important; 
}
.ad-plea
{
display:none;
}
    
body
{
	background: linear-gradient(to bottom, #d7eed1 0px,#f2ffee 200px);
    background-color:#f2ffee;
    color:#000!important;
}
.quoteLink, .quotelink, .deadlink
{
    color:#818544!important;
    font-weight:bold!important;    
}
a, #board-list a, #board-list a:visited
{
	color:#818544!important;
	font-weight:bold!important;
}
.postNum a
{
    color:#3F5C34!important;
}
a:visited
{
	color:#899053!important;
	font-weight:normal!important;
	font-style:italic!important;
}
a:hover, div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover
{
	color:#899053!important;
}
div.reply
{
	background-color:#D5E4D4!important;
	border-right-color:#bfd9b7!important;
	border-bottom-color:#bfd9b7!important;
}

div.reply.highlight
{
    background-color:#DEEDD3!important;
}
div.sideArrows
{
    color:#C1D9B7!important;
}
div.post div.postInfo span.subject
{

}

div.post div.postInfo span.nameBlock span.name
{
	color:#117743!important;
	font-weight:800!important;
}

div.post div.postInfo span.nameBlock span.postertrip
{
    color:#228854!important;
}
a.useremail[href="mailto:sage"]:after {
    content:" (sage)";
}
.useremail .name, .useremail .postertrip
{
	color:#b22caa!important;
}
blockquote > span.quote
{
	color:#789922!important;
	font-style:italic!important;
}


table.postForm > tbody > tr > td:first-child
{
    background-color:#5d9256!important;
}
table.postForm > tbody > tr.rules > td
{
    background-color:transparent!important;
}
.postingMode
{
    display:none!important;
}


span.summary
{
    color:#000000!important;
}

hr
{
    border-color:#C1D9B7!important;
}
div.pagelist
{
    background-color:#daf0d6!important;
    color:#4B345C !important;
    border-right-color:#bdd9b7;
    border-bottom-color:#bdd9b7;
}
div.pagelist a
{
    text-decoration:none!important;
}
#boardNavDesktopFoot, #boardNavDesktop, #boardNavDesktopFoot a, #boardNavDesktop a, div.pagelist strong
{
    color:#3F5C34!important;
}
/* ads */
div[style="text-align: center"], div[style="text-align: center;"]
{
	display:none!important;
}

}
