@-moz-document regexp("https?://www\\.google(\\.com?)?(\\.[a-z][a-z])?/(|\\?gws_rd.*|\\?gfe_rd.*|webhp\\?.*)")
{
  .k1zIA
  {
    background-image: url(https://w7.pngwing.com/pngs/817/902/png-transparent-google-logo-google-doodle-google-search-google-company-text-logo-thumbnail.png) !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-size: 275px 95px !important;
    height: 333px;
    width: 280px;
  }
/*border between quick results and search */
  .lnXdpd , .xtSCL,.FqnKTc
  {
    display: none;
  }

  body
  {
    background: url("https://i.pinimg.com/564x/d6/f7/3c/d6f73c828856ee046b5d01343e739457.jpg") center center !important;
    background-size: cover !important;
  }

  #hptl a
  {
    display: inline-block;
    padding-left: 15px;
    color: #fff;
    opacity: 0.75;
  }

  .gbw >div >div
  {
    overflow-y: hidden;
  }

  iframe
  {
    visibility: visible;
  }
/*color hint*/
  .WggQGd, .wM6W7d
  {
    color: #ffffff !important;
  }

  .mus_tt20
  {
    color: #dcdcdc !important;
  }

  .lst
  {
    color: white !important;
  }
/*text in search box*/
  .gLFyf
  {
    color: rgba(255, 255, 255, 0.87);
  }

  a
  {
    color: #d0d0d0 !important;
    text-decoration: none;
  }

  #gb > div> a
  {
    color: #fff!important;
  }

  .gb_qa svg, #gbg6 #gbi4t, #gbg4 #gbgs4d, #prm-pt, #ftby, #ftby a:visited, #prm-pt a, .ftl, #footer a, div.gb_5a, a.gb_b, div.gb_7a, #gb#gb a.gb_b, #gb#gb a.gb_c, #gb#gb a.gb_b, #gb#gb a.gb_c, .gb_8a, .gb_9a, #gb#gb a.gb_e, #gb#gb a.gb_f, .gb_bb, .gb_cb, #gb#gb a.gb_s, #gb#gb a.gb_s:hover, .gb_g, #gb#gb a.gb_x, .fbar, .gb_Eb,.sbl1
  {
    color: #ffffff !important;
    border-color: #F5F5F5 !important;
  }

/* searchbar*/
  .RNNXgb, .sbibod
  {
    background-color: rgb(15,15,15)!important;
    border: none!important;
    border-top-color: none!important;
    height: 48px !important;
    vertical-align: top !important;
  }
/*lents*/
  .wFncld
  {
    color: #ffffff !important;
  }
/* clear */
  .lBbtTb
  {
    height: 100%;
    color: #d1d7dc!important;
  }
/*list*/
  .aajZCb
  {
    background-color: rgba(0, 0, 0, 0.65) !important;
  }
/*intlist*/
  .erkvQe li
  {
    height: 38px;
  }

  .sbl2
  {
    color: #e2e2e2;
  }
/*hover*/
  .sbhl
  {
    background: #0e0e0e57 !important;
  }
/*buttons*/
  .FPdoLc input[type="submit"], .sbdd_a input
  {
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 0.27), rgba(0, 0, 0, 0.28)) !important;
    background-color: rgba(255, 255, 255, 0) !important;
    border: 1px solid rgba(0, 0, 0, 0.06) !important;
    color: #fff !important;
    cursor: pointer;
    height: 38px;
    line-height: 38px;
    margin: 11px 6px;
    min-width: 54px;
    padding: 0 8px;
    text-align: center;
  }

  .FPdoLc input[type="submit"]:hover, .sbdd_a input:hover
  {
    background-image: -webkit-linear-gradient(top, rgba(0, 0, 0, 0.0), rgba(0, 0, 0, 0.0)) !important;
    background-color: rgba(15, 15, 15, 0) !important;
  }

  #gws-output-pages-elements-homepage_additional_languages__als
  {
    display: none;
  }

  #hplogo
  {
    display: none !important;
  }

  #mw, #cnt
  {
    background: white !important;
  }

  #footcnt
  {
    visibility: hidden !important;
  }

  .gb_C .gb_D
  {
    background-position: -465px -35px !important;
  }

  .gb_Rb .gb_D
  {
    background-position: 0px -35px;
  }

  .mus_tt17
  {
    color: #b1b1b1 !important;
    font-size: 20px;
  }

  .HPVvwb
  {
    background: url(data:image/gif;base64,)
    no-repeat
    left center !important;
    opacity: 0.5;
    background-size: 14px 19px !important;
  }



/* 06/04/2020 */

  body
  {
    color: #fff !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(2)
  {
    padding: 1px;
    background: #0c0c0c80 !important;
    border: 0 solid #dfe1e5 !important;
  }

  form > div:nth-child(2) > div:first-child > div:nth-child(3)
  {
    padding: 0px;
    background: #0c0c0c00 !important;
    border: 0 solid #dfe1e5 !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(2) > div:first-child   > div:nth-child(3)   > div:nth-child(4) > span:nth-child(2)
  {
    border-left: 1px solid #656565 !important;
  }

/*quick search */



  form > div:nth-child(2) > div:first-child  > div:nth-child(3) > div:nth-child(3) > ul > li > div > div:nth-child(2) > div
  {
    color: #ffffff !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(3) > div:nth-child(3)
  {
    background: #000000b8 !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(3) > div:nth-child(3)  a
  {
    color: #fff !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(3) > div:nth-child(3) > ul > li:hover
  {
    background: #74747457 !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(3) > div:nth-child(3) > ul > li >div > div:nth-child(3) > div:hover
  {
    color: #fff !important;
  }

  form > div:nth-child(2) > div:first-child  > div:nth-child(3) > div:nth-child(3) > div:first-child
  {
    border-top: 1px solid #0303034d !important;
  }

  body >  div:nth-child(4) >  div:nth-child(7)  > span > center >   div:nth-child(3)>   div:nth-child(2)
  {
    font-size: small;
    margin-bottom: 24px;
    position: fixed;
    bottom: 0;
    right: 28px;
    /* width: 100%; */
   /* text-align: right; */;
  }

  body >  div:nth-child(4) >  div:nth-child(7)  > span > center >   div:nth-child(3)>   div:nth-child(2) > div:nth-child(2)
  {
    /* display: inline-block; */
    line-height: 28px;
    background: #0000006e;
    padding: 6px;
    border-radius: 24px;
    /* bottom: 25px; */
    /* position: absolute; */
    margin: 0 auto;
    width: fit-content;
  }

/*05/07/2020*/
  #searchform > div
  {
    display: none !important;
  }

  .HPVvwb > :nth-child(1),.HPVvwb > :nth-child(2),.HPVvwb > :nth-child(3),.HPVvwb > :nth-child(4)
  {
    display: none !important;
  }

  .c93Gbe
  {
    display: none;
  }

/*DARK MODE FIX 26/09/2021*/
  #gb
  {
    background-color: rgba(255, 255, 255, 0) !important;
  }
}

@-moz-document regexp("https?://ogs\\.google\\.?.*")
{
  html > body > div > c-wiz > div > div > c-wiz > div > div
  {
    background: #00000030 !important;
   /* border: 1px solid rgba(0,0,0,0.2); */
   /* box-shadow: 0 2px 2px 0 rgba(0,0,0,0.14), 0 3px 1px -2px rgba(0,0,0,0.12), 0 1px 5px 0 rgba(0,0,0,0.2); */
    height: 900%;
    overflow-x: hidden;
    position: relative;
    width: 100%;
  }

  ul
  {
    top: 0;
  }

  ul > li > a:hover
  {
    background: #0000003d !important;
  }

  ul > li > a:hover span
  {
    background-color: #f6fafe00  !important;
    text-decoration: none;
    color: #fff;
  }

  ul > li > a > span
  {
    background-color: #f6fafe00;
    text-decoration: none;
    color: #f2f2f2  !important;
  }

  html > body > div > c-wiz > div > div > c-wiz > div > div > div > a
  {
    background-color: #f6fafe00 !important;
    ;
    border: 1px solid #dadce0;
    color: #f2f2f2 !important;
    ;
  }

  html > body > div > c-wiz > div > div > c-wiz > div > div > span
  {
    border-bottom: 1px solid #ffffff36 !important;
  }

  ::-webkit-scrollbar
  {
    width: 12px !important;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #00000000  !important;
    background-clip: padding-box;
    border: 1px solid #ffffff5c  !important;
    border-radius: 8px;
    box-shadow: none;
    min-height: 35px;
    width: 10px  !important;
  }
}
