css:
/* I got tired of looking at the ugly forum colors.  Feel free to modify */
/* More can be done for sure.                                            */

/* by: kwliving@gmail.com */
/*                        */
/* copied from plenty of fish purple */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.plentyoffish.com"), url("http://pics.plentyoffish.com")
{
.normalTextSmall, .normalTextSmallBold, .normalTextSmaller {
     font-family: Tahoma,Verdana,Arial,Helvetica,sans-serif !important;
     color:#000 !important;
     text-shadow:1px 1px 1px #999 !important;
}
body { 
     font-family: Tahoma,Verdana,Arial,Helvetica,sans-serif !important;
     background-image:url("data:image/jpeg;base64,") !important;
     background-color: #E0B5BC !important;
     color: #222222 !important;}

a {color: #9c0063 !important;  }
a:link { color: #9c0063 !important; }
a:visited { color: #9c0063 !important; }
a:active { color: #9c0063!important; }
a:hover{ color: #efd3e7 !important;
    background-color: #9c0063 !important; }

td {
     font-family: Tahoma, Verdana, Arial, sans-serif !important;
     color: #141414 !important; }

.TableHeaderText { 
     color: black !important; 
     background-color: #fff0bf !important; 
     font-size: 14px;}

.forumHeaderBackgroundAlternate { background-color: #ffc9dd !important; color: #141414 !important;}
.forumAlternate {background-color: #ebd3e7 !important; color: black !important; }
.forumRow {background-color: #ffc9dd !important; color: black !important;}
.forumRowHighlight {
     background-color:#ebd3e7 !important; 
     color: #141414 !important;
}

a.normalTextSmallBold {
	background-color: #f5bff2 !important;
	color: #333 !important;
}
.quote {
     background-color: #e0b5bc !important;
     border:2px solid gray;
     padding:3px;
     color: #141414;
     font-style: italic;
     margin-left: 30px;
     line-height: 1 !important;
     text-shadow:1px 1px 1px #333 !important;
}
.quote::before {
     font-family: serif;
     font-size: 24px;
     content: "\201c \00a0";
}
.quote::after {
     font-family: serif;
     font-size: 24px;
     content: "\00a0 \201d";}

/* Eliminate banner */
     body img[src$="forumhead.jpg"] { display: none; }

select {
     background-color: #9c0063 !important;
     color: pink !important;
     font-size: 13px !important;
}
input { 
     -moz-appearance: none !important;
     background: transparent !important;
     background-color: #9c0063 !important;
     border: purple 2px solid;
     color: pink ;
}
input[type="submit"],input[type="input"] { 
     color: pink !important;
     text-shadow:1px 1px 1px #000000 !important;
     padding: 2px 3px 4px !important;
     -moz-box-shadow:0 0 1em 0 #9c0063; 
}
textarea {
     -moz-appearance: none !important;
     background-color: pink !important;
     color: black !important;
     font-family: Tahoma,Verdana,Arial,Helvetica,sans-serif !important;
     font-size: 9pt !important;
}
a.linkSmall {
     padding-left: 4px;
     padding-right: 4px;
     padding-bottom: 1px;
     display: inline-block;
     text-align: center;
     color: #9c0063 !important;
     text-decoration: none;
}
body table tr td {
	background-image: none !important;
	color: #222 !important;
}
body table tr td a {
	color: #909 !important;
        text-decoration: none  !important;
        display: inline-block !important;
        padding: 2px !important ;
        margin: -1px !important;
}
body table tr td a:hover {
	background-color: white !important;
        color: #9c0063 !important;}

body table tr td a font {
	color: #9c0063 !important;
}
}
