css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
h3{text-shadow: 0 0 0 transparent!important;}
.tt-a{padding:40px!important; padding-bottom:60px!important;}

.browse2-results, #deviation, .mods-hh, .gtab-selected, .mczone, #chat , .forumThread, #thread, #news, .settings_form, #output .bubbleview{background: #1a1a1a none!important;border: 0 solid transparent!important;}

#heyboxContents, #heyboxShadow, #heybox, #facetMenu-Category, #deviation-links, .hh, .gr-body, #artist-comments, .gr, .gr-top-alone,#gmi-CComment .ch-ctrl, .blues-bar, .blues-bar-ctrl, .iconset-gruser .f, .gtab-selected, #super-secret-why, .pbox, #dailyDevTopNav, #journal-tab-journaltext, .mczone-title, .settings_wrapper, ul.menu_holder li, .brab, .ccomment-post .ch-ctrl {background: #1d1d1d none!important;border: 0 solid transparent!important;color:#515151!important;}


#browseNavbar, #facetMenu-Browse, .f, #dailyDevs, .deviationSubmission, .mczone-inner .ch-ctrl, th.l.f *, #browsebar2, #pagemenu{background: #1f1f1f none!important; border: 0 solid transparent!important;}

.gr-top, .f, .h{border:0 solid transparent!important;}

.sliding-menu ul *, #facetMenu-Browse_list li a, .social a{color: #94ae13!important;}
.sliding-menu ul *:hover,  #facetMenu-Browse_list li a:hover{color: #1f1f1f!important;}
#facetMenu-Browse_list li a.selected{color: #94ae13!important;}
.noncollapseHeader, .altaltview, .talkmessage-taller, .mcb-body, .header, .talkmessage{color:#616161!important; text-shadow:0 0 0 #616161!important;background:#2f2f2f none!important;border: 0 solid transparent!important;}
strong.c, .gr h2{color:#616161!important; text-shadow:0 0 0 #616161!important;border:0 solid transparent!important;}


.tabInactive, a.a, a.t,a.u, #deviation-links a, .text a, .pagescroll a, a.h{color:#94ae13!important; text-shadow: 0 0 0 transparent!important;text-decoration:none!important;}
.tabInactive:hover, a.a:hover, a.t:hover, a.u:hover, #deviation-links a:hover, .text a:hover, .pagescroll a:hover, .social a:hover, a.h:hover, .brab:hover{color:#1f1f1f!important;background:#94ae13!important;text-decoration:none!important;}
.tabActive, a.selected, a.h.active{color:#acca1c!important; text-shadow: 0 0 0 transparent!important;}
.tabActive:hover, a.selected:hover, a.h.active:hover{color:#acca1c!important; background:transparent none!important;}

small a:hover, a.emoticons:hover, .mcb-title a:hover, .mcb-collection a:hover, .mcb-line a:hover{color:#1a1a1a!important; background:#3B5A4A none!important; text-decoration:none!important;}

#catBar, .catbar, .iconset-gruser .f, .messages-head{background: #222 none!important;border-color:#252525!important;}
.catTitle{color: #515151!important;}
.catTitle a{color:#94ae13!important;}

.shadow img{border:5px solid #101010!important; -moz-border-radius:5px!important;}
.shadow img:hover{border-color: #acca1c!important;}

#output, html{background-color:#252525!important;}
#top{background:#202020 none!important;}

.ch.bubbleprefix, #deviation-links i, .gr-headless i, .gr-ccomments-box i{display:none!important;}


#moremenu li a, #moremenu-h, #moremenu a{background-color: #202020!important; color:#94ae13!important;}
#moremenu li a:hover{color:#202020!important; background: #94ae13!important;}
i.i21{background:#202020 none!important; color:#515151!important;}
#moremenu li a i, .browse2-feeds, .feedbutton, .frontbutton{background-color:transparent!important;}

ul.sitemap a i, a.sitemap i{
position: absolute;
width: 30px;
margin-top: -2px;
margin-right: 0pt;
margin-bottom: 0pt;
margin-left: -35px;
height: 160px;
background-image:url(http://trezoid.webs.com/dAnu.gif)!important;
background-color:transparent!important;
}

#depths{background: #141414 none!important;}

#mcstatus{background: #141414 none!important;}
.pagescroll .f, .moodbox, .page2, #moremenu a {background:#202020 none!important;}


i.c, i.gr1, i.gr2, i.gr3, i.gr4, .tri, .gtab-i, i.l, .mm, .dd{display:none!important;}

#popup2 *{color:#141414!important;}


.f .text{color:#616161!important;padding:20px!important;}

.f .a{background-color:#1a1a1a!important;padding-bottom:5px!important;}

.gr-configbox .gr-top .gr{background: #498091 none!important;}
.gr-configbox .gr-body .gr{background: #2f2f2f!important;}

.mcb-midview .tt-a{padding:5px!important;}

.gallery-stream-row{height: 300px!important;}

#gmi-ResourceTV .tt-a{padding:3px!important;}

a.gmbutton2{left:0px!important;}

#output tr td{background:#1d1d1d none!important;}

#footerThumbData{height:300px!important;}

img.lit{border:0 none transparent!important;}
}
