css:
@-moz-document domain("pinterest.com"), domain("pinterest.de"), domain("pinterest.es") {
/* Remove Login Prompts*/

body,
mains,
article {
    overflow: auto !important;
    height: 100% !important;
}

/* Hide Header / Footer */
.outerCookieBar,
div[data-test-id="unauthEuCookieBar"],
div[data-test-id="giftWrap"] {
    display: none !important;
    visibility: hidden !important;
}

/* Hide Login */
div[data-test-id="login"] {
    display: none !important;
    visibility: hidden !important;
    background-color: red !important;
    color: red !important;
}

div[data-test-id="fullPageSignupModal"],
div[data-test-id="fullPageSignupModal"] > div:nth-child(1) {
    overflow: auto !important;
    background-color: transparent !important;
    opacity: 1 !important;
    z-index: 0 !important;
    color: red !important;
}

div[data-test-id="fullPageSignupModal"],
#credential_picker_container {
    display: none !important;
    visibility: hidden !important;
}


/* ------------------- */
/* Enable mouse Interaction */

*[aria-disabled="true"] {
    border:red;
    opacity:0.5;
}

* {
    pointer-events: auto !important;
}

/* ------------------- */


div[data-layout-shift-boundary-id="CloseupPageContainer"] {
    padding:0px;
    background-color:#fff;
}

div[data-test-id="breadcrumbs"],
div[style*="x: 99999"] {
    display:none;
}

div[data-test-id="unauth-header"] > div > div:nth-child(1),
div[data-test-id="unauth-header"] > div > div:nth-child(3),
div.OpenTypeahead {
    display:none;
}

/* Search Box */
div[data-test-id="unauth-header"] > div {
    background:#323537;
}

div[data-test-id="unauth-header"] > div > div:nth-child(2) form[name="search"] > div > div {
    border:hsl(204, 5%, 26%) 1px solid;
    border-radius: 0px;
    background:hsl(204, 5%, 16%);
}

form[name="search"] input {
    color:hsl(204, 5%, 80%) !important;
}

form[name="search"] input::placeholder {
  color: hsl(204, 5%, 16%);
}

/*----------- */


div[style="cursor: pointer; z-index: 1;"] {
    display:none;
}

div[data-test-id="closeup-body-landscape"] > div,
div[data-test-id="closeup-body-landscape"] > div > div {
    max-width:100%;
    width:100%;
    height:600px;
    display:block;
    background: #323537;
}

/* Main */
div[data-layout-shift-boundary-id="CloseupPageBody"],
div[data-test-id="CloseupMainPin"],
div[closeup-body-landscape] {
    height:600px !important;
    margin:0px;
    display:block;
}

div[data-test-id="callToActionButton"] {
    background-color:#fff;
    color:black !important;
    width:auto;
    height:1em;
    display:inline-block;
    border:0px solid orange;
}

div[data-test-id="callToActionButton"] > div > div >span  {
    display:none !important;
}


/* Main Details*/
div[data-test-id="CloseupDetails"] {
    background:white;
    border-right:1px solid #373737;
    width:450px !important;
    height:600px !important;
    display:block;
    overflow:hidden;
    position:absolute;
    top: 0px;
    left: 0px;
    margin-left:0px !important;
    border-bottom:1px solid #eaeaea;
}

div[data-test-id="CloseupDetails"] div[data-test-id="callToActionButton"] {
    background:#20b050;
    position: absolute;
    top:0px;
    left:0px;
    padding:10px 0px 30px 18px;
    vertical-align:middle;
    display:block;
    width:450px;
    border-bottom:1px solid white;
}

div[data-test-id="CloseupDetails"] div[data-test-id="callToActionButton"]:hover {
    background:#107933;
}

div[data-test-id="CloseupDetails"] div[data-test-id="callToActionButton"] * {
  color:white !important;   
}

div[data-test-id="pin-domain-link"] > div {
    width:450px;
    height:30px;
    padding-top:10px;
    padding-left:22px;
    position:absolute;
    left:-5px;
    top:0px;
}

div[data-test-id="CloseupDetails"] > div {
    padding-top:40px;
}

/*  -- Missappropriations and social-mediafication of usurped data */
div[data-test-id="CloseupUserRep"],
div[data-test-id="CloseupDetails"] > div > div:nth-child(3),
div[data-test-id="CloseupDetails"] > div > div:nth-child(4),
div[data-test-id="CloseupDetails"] > div > div:nth-child(5),
div[data-test-id="CloseupDetails"] > div > div:nth-child(9),
button[data-test-id="more-information-button"] {
    display:none;
}


/* Main Image */
div[data-test-id="CloseupMainPin"] > div > div > div > div > div,
div[data-test-id="CloseupMainPin"] > div > div > div > div:nth-child(1) > div > div > div > div > div {
    border: 0px solid orange;
    width:100% !important;
    
}

div[data-test-id="pin-closeup-image"] {
    display:block;
    overflow:visible;
    border-radius:0px;
    position: absolute;
    left:0px;
}


div[data-test-id="pin-closeup-image"] > div > div {
    background-color:transparent !important;
    display: block;
    max-width:100%;
    width: 100%;
    height: 600px;
    overflow: hidden;
    position: absolute;
    left: 448px;
    top: 0px;
    padding: 0px !important;
}

/* Primary Image */
div[data-test-id="CloseupMainPin"] div[style="user-select: none;"] {
/*     user-select: auto !important; */
}

div[data-test-id="pin-closeup-image"] > div > div > img {
    max-width:100%;
    height: auto;
    width:auto;
    display:block;
} 

div[data-test-id="pin-closeup-image"] > div[style^="background-color:"] {
    position:absolute;
    left:0px;
    top:0px;
    z-index:1000;
    display:block;
    background:blue;
    border:orange;
    width:500px;
    height:500px;
    display:none;
}


/* Gallery */

div[data-layout-shift-boundary-id="PageContainer"] {
    margin-top:40px;
}

#relatedPins,
div[style="top: 80px; z-index: 2;"] {
    display:none;
}

/*div[role="listitem"] {
    height:450px !important;
    background-color: hsl(240deg 6% 73%);
    margin:0px;
    padding:5px;
    display:inline-block;
    position:relative;
    left:0px !important;
    top:0px !important;
    transform: translateX(0px) translateY(0px) !important;
    vertical-align:middle;
}*/

/* Ad Button for additional stolen content */
div[data-test-id="back-button"] {
    display:none;
}

div[data-test-id="pincard-image-with-link"],
div[data-test-id="pin-visual-wrapper"] > div,
div[data-test-id="pincard-image-without-link"],
div[data-test-id="pincard-product-with-link"],
div[data-test-id="pincard-storyPin-without-link"],
div[data-test-id="pincard-storyPin-with-link"] {
    border-radius:0px !important;
}

/* Directory Pins*/
div[data-test-id="pincard-storyPin-without-link"] > div:nth-child(1),
div[data-test-id="pincard-storyPin-with-link"] > div:nth-child(1) {
    border-radius:0px;
    background-color:white !important;
    margin:0px;
    width:40px;
    height:40px !important;
    text-align:center;
    padding:6px;
}

div[data-test-id="pin"] > div > div:nth-child(2),
div[data-test-id="search-suggestion"] { /* Social Media Noise */
    border:1px solid blue;
    position:absolute;
    display:none;
}

/* Advertisements */
div[data-test-id="related-pins-similar-ideas-title"],
a[href^="/ideas/"],
div[style*="background-color: rgba(51, 51, 51, 0.8)"],
div[data-test-id="pin"] > div:nth-child(2) {
    display:none !important;
    border:1px solid orange;
}
}
