css:
@-moz-document url-prefix(https://timetable.fit.cvut.cz) {

body {
    background: #323232 !important ;
    color: #333;
    cursor: auto;
    font-family: Roboto,sans-serif;
    font-style: normal;
    font-weight: 400;
    line-height: 160%;
    margin: 0;
    padding: 0;
    position: relative;
}

#fittable .table .day:nth-child(odd) {
    background: #242424;
}

#fittable .table .day {
    max-height: 960px;
    background: #323232;
    color: #0079c1;
    height: 960px;
    width: 20%;
    float: left;
}

.footer a {
    color: #fff;
    font-weight: 700;
    line-height: 16px;
}

.footer {
    line-height: 16px;
    font-size: 13px;
    color: #fff;
    padding-top: 4em;
}

#fittable .table .events .event.is-opened .detail {
    visibility: visible;
    opacity: 1;
    min-height: 180px;
    height: auto;
    margin: 0 0 5px;
    background: #262626;
}

#fittable .table .events .event .detail .basic-props .location button {
    color: #e1e1e1;
}

#fittable .table .events .event .detail .num-props .num-prop .value {
    color: #e1e1e1;
    font-size: 15px;
}

div.teacher {
    color: #fff;
    margin: 0;
    padding: 0;
}

#fittable .table .events .event .detail .teachers .reveal i.fa:first-of-type {
    color: #e1e1e1;
    margin-left: -32px;
}

#fittable .table .events .event .detail .teachers .reveal i.fa {
    color: #e1e1e1;
    margin-right: 8px;
}

#fittable .table .events .event .detail button {
    color: #0079c1;
    line-height: inherit;
    text-decoration: none;
}

span {
    color: #e1e1e1;
}

::-webkit-scrollbar {
  width: 10px;
}
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 8px #585858;
  border-radius: 10px;
}
::-webkit-scrollbar-thumb {
  background: grey;
  border-radius: 5px;
}
::-webkit-scrollbar-thumb:hover {
  background: #585858;
}
::-webkit-scrollbar-track-piece{
  background: #585858;
}


}
