css:
@-moz-document url-prefix(https://timetable.fit.cvut.cz)
{
  body.with-line {
    border-top: 3px solid #b14c4c;
  }
  
#fittable .LogoutButton {
    background: #b14c4c;
    color: #fff;
    text-transform: uppercase;
    padding: .5rem 1rem;
    float: right;
}

#fittable .events .event .inner:hover {
    background-color: #9d4f4f;
}

header .header-usermenu-username i.fa {
    color: #b14c4c;
    margin-right: .7rem;
}

  [data-reactid|=".0.0.0:0.0.0.3"] {
    color: #d14c4c;
}
  
  header h1 {
    color: #d14c4c;
    font-weight: 300;
    margin: -10px 0 0;
}
  
  header h1+.sub-header {
    text-transform: uppercase;
    font-size: 12px;
    margin-top: -7px;
    color: #d14c4c;
    font-weight: 400;
}
  
  #fittable .week-nav-btn {
    background: #d14c4c;
    width: 35px;
    height: 35px;
    color: #fff;
    text-align: center;
    line-height: 35px;
    font-size: 14px;
    -webkit-transition: background .2s;
    transition: background .2s;
    text-transform: uppercase;
}
  
  .SemesterWeek-text {
    float: left;
    position: relative;
    margin-left: 15px;
    color: #d14c4c;
    line-height: 35px;
    text-transform: uppercase;
}
  
  #fittable .function-btn {
    color: #d14c4c;
    background-color: #fff;
    -webkit-transition: background .2s;
    transition: background .2s;
    padding: 0 8px;
    height: 35px;
    line-height: 35px;
    position: relative;
    z-index: 199;
    background: transparent;
}
  
  line.Grid-line {
    stroke-width: 1;
    stroke: rgb(209, 76, 76);
}
  
  body
  {
    background: #323232 !important;
    color: #333;
    cursor: auto;
    font-family: Roboto,sans-serif;
    font-style: normal;
    font-weight: 400;
    line-height: 160%;
    margin: 0;
    padding: 0;
    position: relative;
  }

  #fittable .table .day:nth-child(odd)
  {
    background: #242424;
  }

  #fittable .table .day
  {
    max-height: 960px;
    background: #323232;
    color: #0079c1;
    height: 960px;
    width: 20%;
    float: left;
  }

#fittable .events .event .inner {
    background: #9b3e3e;
    color: #fff;
    height: 100%;
    -webkit-transition: background .2s;
    transition: background .2s;
    overflow: hidden;
    position: relative;
    line-height: 1.35;
}

  .footer a
  {
    color: #fff;
    font-weight: 700;
    line-height: 16px;
  }

  .footer
  {
    line-height: 16px;
    font-size: 13px;
    color: #fff;
    padding-top: 4em;
  }

  #fittable .table .events .event.is-opened .detail
  {
    visibility: visible;
    opacity: 1;
    min-height: 180px;
    height: auto;
    margin: 0 0 5px;
    background: #262626;
  }

  #fittable .table .events .event .detail .basic-props .location button
  {
    color: #e1e1e1;
  }

  #fittable .table .events .event .detail .num-props .num-prop .value
  {
    color: #e1e1e1;
    font-size: 15px;
  }

  div.teacher
  {
    color: #fff;
    margin: 0;
    padding: 0;
  }

  #fittable .table .events .event .detail .teachers .reveal i.fa:first-of-type
  {
    color: #e1e1e1;
    margin-left: -32px;
  }

  #fittable .table .events .event .detail .teachers .reveal i.fa
  {
    color: #e1e1e1;
    margin-right: 8px;
  }

  #fittable .table .events .event .detail button
  {
    color: #bd7676;
    line-height: inherit;
    text-decoration: none;
  }

a {
    color: #c77d7a;
    line-height: inherit;
    text-decoration: none;
}

  span
  {
    color: #e1e1e1;
  }

  ::-webkit-scrollbar
  {
    width: 10px;
  }

  ::-webkit-scrollbar-track
  {
    box-shadow: inset 0 0 8px #585858;
    border-radius: 10px;
  }

  ::-webkit-scrollbar-thumb
  {
    background: grey;
    border-radius: 5px;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    background: #585858;
  }

  ::-webkit-scrollbar-track-piece
  {
    background: #585858;
  }

p, p.lead {
    color: #e1e1e1;
    line-height: 1.6;
}

#fittable-landing h1.hero {
    color: #d14c4c;
    font-size: 50px;
    font-weight: 100;
    margin-top: 0;
}

.sub-hero {
    color: #e1e1e1;
}

#fittable-landing .top-part button.login {
    -webkit-appearance: none;
    -moz-appearance: none;
    border-radius: 0;
    cursor: pointer;
    font-family: Roboto,sans-serif;
    font-weight: 400;
    line-height: normal;
    margin: 0 0 1.25rem;
    position: relative;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    padding: 1rem 2rem 1.0625rem;
    font-size: 1rem;
    background-color: #d14c4c;
    border: 0 solid #00619a;
    color: #fff;
    -webkit-transition: background-color .3s ease-out;
    transition: background-color .3s ease-out;
    font-weight: 700;
    margin-bottom: 7px;
    display: block;
}
#fittable .table .day.active .label-wrap {
    border-top: 3px solid #ffe200;
}
#fittable .Search-input input:active, #fittable .Search-input input:focus, #fittable .Search-input input:valid {
    color: #000000!important;
}

#fittable .Search-result {
    background: #323232;
}
#fittable .Search-input input:active, #fittable .Search-input input:focus, #fittable .Search-input input:valid {
    background: #5c5c5c;
}

#fittable .Search-results {
    background: #9b3e3e;
}


}
