css:
@-moz-document url-prefix(http://www.chambersharrap.co.uk/chambers/features/chref/chref.py/main?lookitup) {
body{background-image:none !important;margin:5px !important}
div.hr{display:none !important}
}
@-moz-document url-prefix(http://en.wikipedia.org/w/index.php?lookitup) {
div#footer,h3#siteSub,div#contentSub,*.infobox.sisterproject{display:none !important}

h1{font-size:16px !important}
a{color:blue!important}
body{padding-left:5px!important}
}

@-moz-document url-prefix(http://www.urbandictionary.com/define.php?lookitup) {
div#subnav1,div#footer{display:none !important}
}
@-moz-document url-prefix(http://images.google.com/images?lookitup) {
div{display:none !important}
img{border:0 !important}
}
@-moz-document url-prefix(http://www.google.com/search?lookitup&q=define) {
div#guser,body>center{display:none !important}
}
@-moz-document url-prefix(http://www.google.com/search?lookitup&hl) {
div#guser,body>center{display:none !important}
}
@-moz-document url-prefix(http://www.answers.com/) {
div.ads,div#headerSection,div#navigationSection,div#footer,table#copyrightTable,p.didYouMean,div#shopping{display:none!important}
div#new_left{margin:0!important;padding:0!important}
}
@-moz-document url-prefix(http://www.m-w.com/cgi-bin/dictionary) {
td.left_col,div.banner_ad,div.hnav_bar,div.search_bar,div.ad_space,div.footer{display:none}
TD>div,div.page_content_box,div.page_content,body{width:100%!important;padding:0!important;margin:0!important;background:none;background-color:white!important;border:0!important}
div.page_content_box>div{display:none}
div.page_content_box>div:first-child{display:block!important;margin:5px!important}
td{border:0!important;background:none!important}
}
@-moz-document url-prefix(http://en.wiktionary.org/w/index.php?lookitup) {
div.footer{display:none!important}
body{padding-left:5px!important}
}
