css:
/* Trello Reduced style */
@-moz-document url-prefix('https://trello.com/') {

/* Hide buttons on menu bar */
#trello-root #surface [data-desktop-id="header"] button[data-test-id="workspace-switcher"] {
    display: none;
}
#trello-root #surface [data-desktop-id="header"] button[data-test-id="recently-viewed-boards-menu"] {
    display: none;
}
#trello-root #surface [data-desktop-id="header"] button[data-test-id="templates-menu"] {
    display: none;
}

/* Hide buttons from board toolbar */
#trello-root #surface main #content .board-header div.board-header-btns.mod-right span.js-pup-dropdown-list-btn {
    display: none;
}
#trello-root #surface main #content .board-header div.board-header-btns.mod-right span.js-butler-header-btns {
    display: none;
}

}
