css:
@-moz-document domain(inoreader.com) {
/* @--General Stuff--@ */

::selection {
    background-color: #116480 !important;
    color: #f2f7f9 !important;
}
::-webkit-scrollbar {
	width: 8px;
	height: 6px;
}
::-webkit-scrollbar-thumb {
    background-color: rgb(45, 55, 71) !important;
}
body {
    background-color: rgb(18, 23, 40);
    color: rgb(237, 239, 245);
}
#sb_tree_part {
    background-image: initial;
    background-color: rgb(18, 23, 40);
}
#subscriptions_buttons {
    height: 48px;
    z-index: 99;
    line-height: 22px;
    display: flex;
}
.inno_toolbar_rp_heading {
    color: #a2abbe;
    font-size: 11px;
}
.icon-all-articles:before {
    visibility:hidden;
}
#sb_rp_mark_read, #sb_rp_mark_read:hover  {
    font-size:11px;
    border: 0 none;
}
.icon-upgrade-feature-checkmark:before {
    visibility:hidden;
}
.inno_toolbar_switcher_button:first-of-type {
    font-size:11px;
    margin-left: 0;
    border:0 none;
}
.inno_toolbar_switcher_button_active {
    background-color: #252c44;
    color: #818a9f;
    height: 14px;
    line-height: 15px;
    text-align: center;
    font-size:11px;
    border: 1px solid #3c475a;
}
#unread_filter_switcher .inno_toolbar_switcher_button.last-btn {
    border-radius: 5px;
    padding: 6px 34px 6px 10px;
}
.inno_toolbar_switcher_button_active:hover {
    border: 1px solid #17dbff;
}

/* @--Search Bar--@ */

#sb_tp_search_overlay {
    background-color: rgb(18, 23, 40);
    border: 1px;
}
#feed_searcher {
    background-color: rgb(37, 44, 68);
    color: rgb(18, 180, 255);
    font-size: 12px;
    height: 25px;
    box-shadow: none !important;
    border: 1px solid #3c475a;
    border-radius: 5px;
    margin-top: 11px;
}
#feed_searcher:hover {
    border: 1px solid #4c586e;
}
#feed_searcher:focus {
    background-color: rgb(37, 44, 68);
    color: rgb(18, 180, 255);
    font-size: 12px;
    height: 25px;
    box-shadow: none !important;
    border: 1px solid #12b4ff;
    border-radius: 5px;
    margin-top: 11px;
}
#wraper.tree_pane_docked #feed_searcher {
    background-color: rgb(37, 44, 68);
    color: inherit;
}

/* @--Article List Column--@ */

.dashboard_wrapper {
    background-color: rgb(11, 15, 28);
}
.dashboard_gadgets {
    background-color: rgb(11, 15, 28);
    font-size: 13px;
}
.gadget_overview_feed {
    background-color: rgb(11, 15, 28);
    border-bottom: 1px solid #1c2333;
    padding-bottom:0;
}
.gadget_overview_feed_title {
    background-color: #0e1221;
    font-size: 11px;
    padding: 12px 15px 12px 12px;
    margin-bottom: 24px;
    border-radius:5px;
}
A.bluelink, A.bluelink:hover {
    font-size:11px;
    color: #17dbff;
}
.dashboard_gadgets .content {
    background-color: rgb(11, 15, 28);
}
.fill {
    background-color: #09243d;
    border: 4px dashed #3b476a;
}

.library_wrapper_outer {
    background: #0b0f1c;
}
.library_section_main_article, .library_secondary_article, .library_section_list_articles {
    background: #0e1221;
    box-shadow: 0 1px 2px rgb(0 0 0 / 80%);
}

#reader_pane {
    background-color: rgb(11, 15, 28);
}
#wraper .reader_pane_view_style_0 .articles_feed_group_heading, #wraper .reader_pane_view_style_2 .articles_feed_group_heading {
    font-weight: bold;
    padding: 6px 6px 6px 6px;
}
#wraper.display_density_3 .reader_pane_view_style_0 .ar:not(.article_expanded), #wraper.display_density_3 .reader_pane_view_style_2 .ar {
    padding: 0px 0px 0px 2px;
    margin:0px;
    border-top: 1px solid #1c2333;
}
.reader_pane_view_style_1 .article_subscribed, .reader_pane_view_style_4 .article_subscribed {
    background-color: #0b0f1c;
}
.reader_pane_view_style_2 .article_expanded {
    background-color: #282e39;
    box-shadow: inset 0 0 0 0px #17dbff, inset 3px 0 0 0px #17dbff;
    margin-bottom: 1px;
}
.reader_pane_view_style_2 .article_expanded:hover {
    background-color: #202639;
}
.reader_pane_view_style_2 .article_unreaded {
    background: #0b0f1c;
}
.reader_pane_view_style_2 .article_unreaded:hover {
    background: #202639;
}
.reader_pane_view_style_2 .article_header_title {
    font-weight: normal;
    font-size: 11px;
    padding-left:2px;
    padding-bottom:0px;
    margin-bottom: 0px;
}
.article_subscribed {
    background-color: #0b0f1c;
    color: #CCCCCC;
    margin-bottom: 1px;
}
.reader_pane_view_style_0 .article:hover, .reader_pane_view_style_2 .article:hover {
    background-color: #202639;
}
.column_view_info {
    font-size:4px;
    width:100% !important;
    padding-top: 0px;
    padding-left:2px;
    padding-right:2px;
    margin-top:0px;
}
.reader_pane_view_style_2 .header_date {
    height: 16px;
    font-size:10px;
}
.column_view_info .popularity_score_span {
    color: #747b8a;
    font-size: 12px;
    padding-left: 5px;
}
span.icon16.popularity_icon_gray {
    color: #12b4ff;
}
span.icon16.popularity_icon_red {
    color: #0092ea !important;
}
.article_sub_title .popularity_div_internal_red {
    padding: 3px;
    border: 0px solid #0092ea ;
}
.article_unread_dot_internal {
    background-color: #0db787;
}
.article_unreaded .article_unread_dot_internal {
    background-color: #dc1f4e;
}
.article_unread_dot_external {
    border: 1px solid #3b476a;
}
.star_img {
    color: #747b8a;
}
.star_img:hover {
    color: #f5d85c;
}
.header_buttons>a>span.icon16 {
    color: #747b8a;
}
.graylink_darker {
    color: #747b8a;
    padding-left: 5px;
}
.article_feed_title {
    display: none;
	font-weight:normal;
	white-space:normal;
	font-size: 10px;
}
.article_tag {
    background-color: #272f49;
    color: #edeff5;
}
.article_full_contents {
    color: #cccccc;
    background-color: #0b0f1c;
}
.article_footer_sticky_border {
    background-color: #0b0f1c;
    border: 0 none;
}
.article_footer {
    height:24px;
    background-color: transparent;
}
.article_expanded .article_footer_placeholder_middle.footerized.article_footer_placeholder_middle_sticky, #article_dialog .article_footer_placeholder_top, #three_way_contents .article_footer_placeholder_top {
    background-color: transparent;
}
.article_footer_main_buttons .article_footer_buttons {
    background-color:#2c3143;
    width: 28px;
    min-width: 28px;
    height: 28px;
    font-size: 18px;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 3px;
    margin: 14px;
    border-radius:5px;
}
.reader_pane_view_style_0 .article_footer_main_buttons .article_footer_buttons, .reader_pane_view_style_1 .article_footer_main_buttons .article_footer_buttons, .reader_pane_view_style_0 .sd .share_wrapper .icon16, .reader_pane_view_style_0 .sd .inno_toolbar_button_wrapper .icon16 {
    border-radius: 5px;
}
.article_footer_placeholder_top .article_footer {
    border-bottom: 0 none;
}
.sd .share_wrapper .icon16, .sd .inno_toolbar_button_wrapper .icon16 {
    background-color:#2c3143;
    width: 28px;
    min-width: 28px;
    height: 28px;
    font-size: 18px;
    display: flex;
    align-items: center;
    justify-content: center;
    padding:3px;
    margin: 14;
    border-radius: 5px;
    position: relative;
}
#three_way_contents {
    background-color: #0b0f1c;
    border-left: 3px solid black;
}
#three_way_contents .article_footer_placeholder_top {
    position: sticky;
    top: 0;
    margin: 20px;
    z-index: 50;
}
/* @--Left Navigation--@ */

.compact_sidebar .parent_div_inner_selected:before {
    background: #17dbff;
    width:3px;
}
#tree_pane, #preferences_categories, .tree_ad {
    background-color: rgb(7, 12, 21);
    color: rgb(185, 179, 170);
border-right: 3px solid black;
}
.compact_sidebar .sidebar_legend {
    padding: 5px 14px 5px 5px;
    margin-bottom:5px;
}
.subscriptions_legend {
    color: #8f97aa;
    font-size: 10px;
}
#subscriptions_nav {
    padding: 0px;
    margin: 0px;
}
.compact_sidebar .parent_div {
    line-height: 28px;
    padding: 0px;
    margin: 0px;
    border-bottom: 1px solid #1c2333;
}
.compact_sidebar .tree_node {
    font-size: 11px;
}
.parent_div_inner_selected {
    background-color: #2b3249;
    color: #f2f4fa;
    border: 0px;
}
.parent_div_inner:hover {
    background-color: #202639;
    color: #f2f4fa;
}

.tree_node.tree_unread {
    color: #eef0f6;
    font-weight: normal;
    font-size:11px;
    opacity: 1;
}
.tree_node
{
    color:#888fa1;
	text-decoration: none !important;
	margin-left: 12px;
	font-weight: 400;
	font-size: 11px;
	opacity: 0.8;
	display: inline-block;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}
.tree_link_folder {
    color:#888fa1;
    font-weight: normal;
    font-size:11px;
    margin-left: 12px;
}
.sidebar_fieldset {
    background-color: rgb(7, 12, 21);
	margin:5px 0 0 0;
	padding:0px 0 0 0;
	border:0 none;
	border-radius: 0px;
	position:relative;
	min-width: 0;
}
.unread_cnt, .unread_cnt_hover {
    color:#888fa1;
    font-size: 10px;
    vertical-align: middle;
    text-align: right;
    cursor: pointer;
    line-height: 20px;
    height: 20px;
}
.ui-sidebar::after {
    visibility:hidden;
}
.ui-sidebar {
    position: fixed;
    width: 4px;
    cursor: col-resize;
    z-index: 100;
    transition: background-color ease-in 0;
    transition-delay: 0.05s;
    height: 100%;
}
.compact_sidebar .add_content_simulated_button {
    font-size: 11px;
    height: 28px;
    margin: 0;
    border-radius: 0;
}
.add_content_simulated_button:hover {
    background-color: #202639;
}
.add_content_simulated_button.expanded {
    background-color: #2b3249;
    box-shadow: inset 0 0 0 0px #17dbff, inset 3px 0 0 0px #17dbff;
}
.add_content_simulated_button_menu_line_item {
    background-color: #202435;
    font-size:11px;
}
.add_content_simulated_button_menu_line_item:hover {
    background-color: #3c435d;
    font-size:11px;
}

/* @-- Content --@ */

body.font_size_-1 .article_title {
    font-size: 16px;
}
.article_sub_title .popularity_div_internal_orange {
    border: 0px none;
}
.feed_name_menu, .author_name_menu, .feed_name_menu span, .author_name_menu span {
    font-size:11px;
    overflow: show;
    text-overflow: ellipsis;
    flex: 1 1 auto;
    min-width: 16px;
}
body.article_alignment_2 .article_content iframe, body.article_alignment_2 .article_content object, body.article_alignment_2 .article_content img {
    max-width: 300px;
display:inline-block;
}
body.line_height_14 .article_content, body.line_height_14 .article_annotation_text {
    line-height: 1.4;
    font-size:11px;
}
.article_card.article_expanded {
    padding-top: 36px;
    padding-bottom: 36px;
    margin-bottom: 24px;
    border: 0px none;
}
div.article_card.article_current {
    border: 0px none;
}
.comment_textarea {
    background-color: #252c44;
    border: 1px solid #3c475a;
}
.comment_textarea:focus {
    box-shadow: none !important;
    border: 1px solid #12b4ff;
    border-radius: 5px;
}

/* @-- Menus --@ */

.inno_toolbar_button_menu {
    background-color: #202435;
    color: #b3b6bd;
    padding:0px;
}
.inno_toolbar_button_menu_item {
    background-color: #202435;
    font-size:11px;
    color: #b3b6bd;
}
.inno_toolbar_button_menu_item:hover {
    background-color: #3c435d;
    font-size:11px;
    color: #b3b6bd;
}
.inno_autocomplete_wrapper {
    background-color: #202435;
    font-size:11px;
    color: #b3b6bd;
    padding:0px;
}
.inno_autocomplete_item {
    background-color: #202435;
    font-size:11px;
    color: #b3b6bd;
}
.inno_autocomplete_item.inno_autocomplete_item_selected {
    background-color: #3c435d;
    font-size:11px;
    color: #b3b6bd;
}
}
