css:
@-moz-document domain("eljur.ru") {
    /* Место для CSS кода... */
    /*     *{
/*         font-family: 'Kurale', serif!important; */
/*         font-weight: 700; 
    } */
    .main{
        background-color: #1D1D1D;
    }
    .layout-main {
    background-color: #1D1D1D;
    border-top: 1px solid #CC397B;
    border-bottom: 1px solid #CC397B;
    }
    .header .logo .logo__title{
        color: black;
    }
    
    .logo {
        box-shadow: 0 0 100px 10px #D52728;
        width: 218px;
        height: 62px;
    }
    
    .logo__right{
        display: none;
    }
    
    .header .logo .logo__subtitle{
        color: #2C0F0C;
    }
    #dnevnik-header{
        background-color: #1D1D1D;
    }
    .ej-accordion--expanded{
        background-color:#2A2A2A!important;
        color: white;
        border: 1px solid #381022;
        border-radius: 10px;
    }
/*     .ej-accordion--expanded:hover{
        transform: scale(1.1);
        transition: 0.4s;
    } */
    .ej-accordion__title {
    color: white;
    }
    .page-title{
        font-size: 20px;   
        color:#20B2AA!important; 
    }
    .ej-accordion{
        background-color: #2A2A2A!important;
    }
    .dnevnik-day--holiday .dnevnik-day__header{
        background-color: #1E0505!important;
    }
    .layout-base{
        background-color: #1D1D1D;
    }
    .navigation-tabs__items .active{
        background-color: #2A2A2A!important;
        color: #F4A016;
    }
    .menu0 .menu0-item--active .menu0-item__title{
        color: #F4A016!important;
    }
    
    ::-webkit-scrollbar {
        background-color: #1D1D1D!important;
    }
    
    ::-webkit-scrollbar-thumb{
        background-color: #42413F!important;
    }
    
    .navigation-tabs__period span.navigation-tabs-label{
        color: #F0EAD6;
    }
    #footer, .layout{
        background-color: #1D1D1D;
        color: white;
    }
    
    .navigation-tabs__item{
        border-color: #CC397B!important;
    }
    .navigation-tabs__item:active{
        border-color: #CC397B!important;
    }
    
    /*Успеваемость*/
    
    .layout-main .layout-content .grid .grid-group .grid-header .inside, .messages-item__author {
        background-color: #1D1D1D;
    }
    .cells{
        background-color: #1D1D1D;
    }
    .cell, .messages-item, .messages-header, .ej-select__header, .ej-select-popup__inner{
        background-color: #1D1D1D!important;
        color: white;
    }
    
    .messages-item__link{
        color: wheat;
    }
    .messages-item__author {
        color: white;
        background-color: inherit;
    }
    
    .cells_header, .grid__scrollbar{
        background-color: #1D1D1D!important;
    }
    .ej-slider__wrap{
        background-color: inherit;
    }
    
    #contextSettings .context__block{
        background-color: #1D1D1D!important;
        color: white;
    }
    .typography h5{
        color: white!important;
    }
    .navigation-tabs__period{
        cursor: pointer;
        margin-bottom: 1px;
        display: flex;
        width: 170px;
        justify-content: center;
        padding: 0 16px 0 16px;
    }
    .navigation-tabs__period:hover{
        border: 1px solid rgba(12,119,195,.25);
        border-radius: 5px 5px 0 0;
        border-width: 1px 1px 0;
    }
    .navigation-tabs__period:active{
        border-color: rgba(12,119,195,.5);
    }
    .navigation-tabs-label{
        color: white;
    }
    
    .login-form__body {
        background-color: #1D1D1D!important;
        color: white!important;
    }
    .ej-form__header{
        color: white!important;
    }
    .ej-form-label span{
        color: white!important;
    }
    
    .login-form{
        background-color: #1D1D1D!important
    }
    
    
    .login-form__body{
        background-color: #2A2A2A!important;
        border-radius: 10px;
    }
    
    #inputrFCiwqz_wxh, #inputDSH1QMiOw4x{
        background-color: #2A2A2A!important;
        color: #F4A016;
        border: 1px solid #CC390B!important;
    }
    
    input.field, .ej-select__input{
        background-color: #2A2A2A!important;
        
        border-color:#CC397B;
    }
    
    
    
    .widget {
        background-color: #2A2A2A!important;
        border: 0;
        color: wheat;
    }   
    
    .widget__header {
        color: white;
    }
    
    .e9RoOsVun9vmXykpRAVW{
        color: #3EA1DF;
    }
    
    .widget__more, .filter-panel{
        background-color: #2A2A2A!important;
        border: 0;
    }
    
    .ej-select__header{
        
    }
    
    .cells .cell.trselect {
        background-color: #68c88887!important;
    }
    
    .cell--hover {
        background-color: #c4b2646e!important;
    }
    
    .fulldate, .null, .cells_header{
        background-color: #1D1D1D!important;
        color: white;
    }
    
    .fulldate {
        padding: 30px 0!important;
        height: 100%;
    }
    
    .notice, table.ej-table thead tr   {
        background-color: #1D1D1D!important;
        color: wheat;
    }
    
    .messages-item:hover {
        background-color: #2A2A2A!important;
    }
    
    .ej-select__header{
        background-color: #2A2A2A!important;
    }
    
    .menu2, tr .left, span.selection-title, .selection a, .schedule__day__content{
        background-color: #1D1D1D!important;
        color: wheat!important;
    }
    .schedule__day__content__lesson, .columns, .ej-form__title, .ej-form-label, .control-label {
        color: white;
    }
    
    .control-checkbox__label{
        color: wheat;
    }
    
    input {
        color: white!important;
    }
    
    .field, .yXIY3sqXtSRbgR6N_5MD, .eR069Hg7azuJYELDr4Fb, .sbS7qnikFTmjnDJ2bL6T, .ej-panel, #table-miss-sum, tr, .strong, .UeM_u8vJ8IwmZff4lQSg, .calendar div, .context__block, .board-item, .board-item__title{
        background-color: #2A2A2A!important;
        color: white;
    }
    
    .board-item__dates{
        color: wheat;
    }
    
    .calendar-day{
        border: 1px solid #f5deb375;
    }

    
    .trhover {
        background-color: #878f5794!important;
    } 
    
    .trselect {
        background-color: red!important;
    }
    
    .trLes:hover{
        background: none;
    }
    
/*     
    .cell-data{
        background-color: red;
    }
 */
