css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xda-developers.com") {

body {
	background: #000000 !important;
	color: #AAAAAA !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0 auto !important;
	padding: 0px;
	width: 1200px !important;
}

input {
	color: #AAAAAA !important;
	background: #000000 !important;
	border: 1px solid #444444 !important;
}

.button input {
	color: #AAAAAA !important;
	background: #000000 !important;
	border: 1px solid #444444 !important;
}

.thread a:hover, .thread a:active, .thread_ahover {
	color: #FFFF33 !important;
	text-decoration: none;
}

.thread a:visited, .thread_avisited  {
	color: #CCCC99 !important;
	text-decoration: none;
}

a, a:hover, a:active, ahover {
	color: #FFFF33 !important;
	text-decoration: none;
}

a:visited, avisited  {
	color: #CCCC99 !important;
	text-decoration: none;
}

fieldset {
	border: 1px solid #444444 !important;
}

.panelsurround {
	background: #000000 !important;
	color: #AAAAAA !important;
}

legend {
	color: #AAAAAA !important;
	background: #000000 !important;
	border: 1px solid #444444 !important;
	padding: 2px 6px !important;
}

strong {
	color: #FFFF33 !important;
	text-decoration: none;
}

hr {
  color: #AAAAAA !important;
  height: 1px solid #444444 !important;
}

.navbar {
	background: #000000 !important;
	color: #AAAAAA !important;
}

.time {
	background: #000000 !important;
	color: #AAAAAA !important;
}

.page {
	background: #000000 !important;
	color: #AAAAAA !important;
}

.page a, .page a:hover, .page a:active, .page_ahover {
	color: #FFFF33 !important;
}

.tborder {
	background: #000000 !important;
	color: #AAAAAA !important;
	border: 1px solid #444444 !important;
}

.tcat {
	background: #000000 !important;
	color: #AAAAAA !important;
	font: bold 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

.tcat a:hover, .tcat a:active, .tcat_ahover {
	color: #FFFF33 !important;
	text-decoration: none;
}

.thead {
	background: #444444 !important;
	color: #AAAAAA !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

.thead a:link, .thead_alink {
	color: #FFFF33 !important;
}

.thead a:hover, .thead a:active, .thead_ahover {
	color: #FFFF33 !important;
}

.tfoot {
	background: #000000 !important;
	color: #AAAAAA !important;
}

.tfoot a:link, .tfoot_alink {
	color: #FFFF33 !important;
}

/*.tfoot a:visited, .tfoot_avisited {
	color: #FFFF33 !important;
}*/

.tfoot a:hover, .tfoot a:active, .tfoot_ahover {
	color: #FFFF33 !important;
	text-decoration: underline;
}

.alt1, .alt1Active {
	background: #000000 !important;
	color: #AAAAAA !important;
	font-size: 9pt;
}

.alt2, .alt2Active {
	background: #000000 !important;
	color: #AAAAAA !important;
}

/* *****alt3 alternating color 3 made by flar ***** */
.alt3 {
background-color:#222222 !important;
}

.inlinemod {
	background: #000000!important;
	color: #AAAAAA !important;
}

.wysiwyg {
	background: #000000 !important;
	color: #AAAAAA !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	margin: 5px 10px 10px 10px;
	padding: 0px;
}

textarea, .bginput {
	background: #444444 !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

select {
	background: #222222 !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

.fjsel {
	background: #000000 !important;
	color: #AAAAAA !important;
}

.fjdpth0 {
	background: #000000!important;
	color: #AAAAAA !important;
}

.panel {
	background: #000000!important;
	color: #AAAAAA !important;
	padding: 10px;
	border: 1px solid #444444 !important;
}

.panelsurround {
	background: #000000 !important;
	color: #AAAAAA !important;
}

.vbmenu_control {
	background: #000000 !important;
	color: #AAAAAA !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	padding: 3px 6px 3px 6px;
	white-space: nowrap;
}

.vbmenu_option {
	background: #000000 !important;
	color: #AAAAAA !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}

.vbmenu_hilite {
	background: #444444!important;
	color: #FFFFFF !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	white-space: nowrap;
	cursor: pointer;
}

#menucontainer {
	display: none !important;
}

}
