/*----------------------------------------------------------*/
/*This section taken from AIOS - Toolbar Skin (Strata40     */
/*compatible) and Modified to clean                         */
/*up any ugly lines and match up all edges                  */
/*----------------------------------------------------------*/

/*makes all addon toolbars transparent*/
/*tested with stumbleupon, adobe contribute,*/
toolbar.chromeclass-toolbar {border: none !important; background: #000!important; text-shadow: #fff 0px 0px 10px, #fff 1px 1px 10px, #fff 1px 1px 3px !important;} 

#aios-toolbar:not([posMode="3"]) {-moz-appearance: none !important; background: #000 !important; -moz-border-radius: 1px !important; padding: 1px 1px 0px 0px !important; border-right: 2px solid !important; -moz-border-right-colors: rgba(245,245,245,0.8) rgba(0, 0, 0, 0.1) !important;}
#aios-toolbar[posMode="3"] {-moz-appearance: none !important; opacity: 1 !important; background-image: none !important; background: #000 !important;}
#aios-toolbar[iconsize="small"] toolbarbutton image {margin-right: -2px !important; padding: 1px !important; min-width: 16px !important;}
#aios-toolbar[iconsize="large"] toolbarbutton image {margin-right: -2px !important; padding-left: 2px !important; min-width: 24px !important;}
#aios-toolbar toolbarbutton dropmarker {display: none !important;}
#aios-toolbar toolbarbutton {margin-bottom: 2px !important; min-width: 24px !important;}
#aios-toolbox-left toolbarseparator, #aios-toolbox-right toolbarseparator {-moz-appearance: none !important; background: RGBa(0,0,0,0.2) !important; margin-top: 4px !important; margin-bottom: 3px !important; border: none !important;}
#aios-toolbox-left {-moz-appearance: none !important; padding: 0px 0px 0px 0px !important; margin-top: -1px !important; border: none !important; background: #000 !important;}
#aios-toolbox-right {-moz-appearance: none !important; padding: 0px 0px 0px 0px !important; margin-top: -1px !important;}
#aios-toggle-toolbox {-moz-appearance: none !important; background: #000 !important; border: none !important;}
#aios-toggle-toolbar *, #aios-toggle-toolbar *:hover {-moz-appearance: none !important; color: transparent !important; margin-top: -1px !important; background-color: rgba(217,217,217,0.8) !important; border-top: 1px solid rgba(219,219,219,0.9) !important; border-right: 1px solid rgba(0,0,0,0.2) !important;}
#aios-sidebarheader-top, #aios-sidebarheader-bottom, #aios-toolbox-sidebartop2, #aios-sidebardivider-1, #aios-sidebardivider-2 {-moz-appearance: -moz-win-glass !important;  background: #000 !important;}
#history-panel > hbox, #bookmarksPanel > hbox, #search, #commandBar, #searchPanel {-moz-appearance: none !important; margin: -1px 0px -1px 0px !important;}
#metatree .tree-bodybox, #imagetree .tree-bodybox, #mediaPreviewBox > vbox, #feedListbox, #permList {-moz-appearance: -moz-win-glass !important; background: transparent !important; border: 1px solid RGBa(0,0,0,0.2) !important; -moz-border-radius: 3px 3px !important; margin: 1px 1px 1px 1px !important;}
#userstyles-heading {-moz-appearance: none !important; background: none !important;}
#styles-filter {-moz-appearance: none !important; background: #fff !important; -moz-border-radius: 4px !important;}
#search #searchbox, #bookmarksPanel > hbox > textbox, #history-panel > hbox > textbox, #searchPanel #searchfield {-moz-appearance: none !important; -moz-border-radius: 4px !important; padding-bottom: 2px !important; margin-top: 1px !important;}
#sidebar-splitter {-moz-appearance: none !important; background: none !important; border-right: 1px solid rgba(0,0,0,0.5) !important; border-top: 1px solid transparent !important; margin-top: 29px !important; padding: 0px 0px 0px 0px !important; max-width: 4px !important; }
sidebarheader {-moz-appearance: -moz-win-glass !important; background: #000 !important; border: none !important;  padding-right: 1px !important;  margin: 0px -10px -1px -1px !important;}
#mediaSplitter, #themeSplitter {-moz-appearance: none !important; border: none !important;}
#sidebars-titlebutton > label {-moz-appearance: -moz-win-glass !important;  color: #000 !important;  opacity: 0.99 !important;  background: none !important;  text-shadow: #fff 0 0 12px, #fff 0 0 12px, #fff 0 0 12px !important;}
#viewButton, #clearListButton, #aios .toolbarbutton-1, #styles-sort, #mainDeck button, #mainDeck .caption-text, #styles button, #extensionsManager toolbarbutton, #extensionsView .addonInstallButton, #styles .button-text {color: #000 !important; border: none !important; background: none !important; text-shadow: #fff 0px 0px 10px, #fff 1px 1px 10px, #fff 1px 1px 3px !important;}
#search-box, .textbox-input, .textbox-input, #search #searchbox .textbox-input-box, #searchPanel #searchfield, #styles-filter {opacity: 0.99 !important;  color: #000 !important;  text-shadow: #fff 0 0 6px, #fff 0 0 6px !important;  margin: 2px !important;}
.aios-tbutton:hover, .aios-tbutton:active, .aios-tbutton[checked="true"], #sidebars-togglebutton:hover, #sidebars-togglebutton:active, #sidebars-togglebutton[checked="true"], #toolbar-button:hover, #toolbar-button:active, #toolbar-button[checked="true"] { background: none !important;}
#downloadManager #search {margin: -3px !important; padding: 2px !important;}
#downloadManager #searchbox {margin: 3px !important;}
#downloadManager #searchbox .textbox-input-box {margin: 1px !important;  opacity: 0.99 !important;  color: #000 !important;}

/* ---------------------------------------------------------*/
/* This code glasses the stumble toolbar                    */
/* ---------------------------------------------------------*/

#downbarMainMenuButton, #downbarClearButton, #downbarHolder {-moz-appearance: none !important; color: #fff !important; opacity: 0.95;}
#downbarMainMenuButton, #downbarClearButton, #downbarHolder .toolbarbutton-text {color: #000 !important; margin-bottom: 1px !important;}
#stumbleupon {-moz-appearance: none !important; border: 0 !important; padding: 2px 2px 3px 2px !important;}
.tabbrowser-tabs tab {background-color: -moz-rgba(255,255,255,0.35) !important;}

/*----------------------------------------------------------*/
/*This code, cleans up the lines left by AiOS               */
/*----------------------------------------------------------*/

hbox {border: none !important; border-bottom: #000 !important;}
hbox#commandBarBottom {-moz-appearance: -moz-win-glass !important; background: transparent !important; background-image: none !important; opacity: .70 !important;}

#browser, #aios-sidebardivider-1 {border-top: 1px #fff !important;}
#aios-toggle-button, #aios-toggle-toolbar, #aios-toggle-button:hover {margin-top: 30px !important; border: none !important; background: transparent !important; -moz-appearance: -moz-win-glass !important;}
#viewGroup {-moz-appearance: none !important; border: none !important; margin: -1px -1px -4px -1px !important;}
#sidebar-splitter, #aios-sidebardivider-3, #aios-sidebardivider-2, #aios-sidebardivider-1 {border: none !important; -moz-appearance: none !important;}
#extensionsManager {-moz-appearance: -moz-win-glass !important; background: #000 !important; opacity: .99 !important; border: none !important; }
#sidebarheader {-moz-appearance: none !important; padding-right: 15px !important; padding-bottom: 5px !important; border-bottom: none !important; background: #000 !important;} /* Got the idea from glass tube*/
toolbar.chromeclass-toolbar#aios-toolbar {max-width: 35px !important; min-width: 35px !important; background: #000 !important; opacity: .99 !important; border: none !important; margin-top: 32px !important;}
tabbrowser#content {margin-top: -2px !important;}
html{border: none !important;}

radiogroup#viewGroup {padding-left: 30px !important; padding-right: 1000px !important; margin-right: -1100px !important;border-top: none !important; margin-top: 1px !important; background: #000 !important; opacity: .99 !important;}
hbox#browser {-moz-appearance: -moz-win-glass !important; background: #000 !important;}

/*----------------------------------------------------------*/
/*This section taken Glass Dialogs. firefox 3.1 and Modified*/
/*----------------------------------------------------------*/

dialog {-moz-appearance: -moz-win-glass !important; background: transparent !important; text-shadow: #fff 0px 0px 10px, #fff 1px 1px 10px, #fff 1px 1px 3px !important;}
dialog * {opacity:0.99 !important;}

/*----------------------------------------------------------*/
/*This section taken Glass menus. firefox 3.1 and Modified*/
/*----------------------------------------------------------*/

popup:not(#autoscroller), menupopup {color: #000 !important; -moz-appearance: -moz-win-glass !important; background: transparent !important; border: none !important; padding: 0px 15px 15px 0px !important; margin: -20px !important; text-shadow: #fff 0px 0px 10px, #fff 1px 1px 10px, #fff 1px 1px 3px !important;}
