@document regexp("https://www.eldarya.*") {
/* AUTHOR: KURU ELDARYA.ES

GLOBAL RULES 
to-do: events, episodes, pet, library, missions, messaging, account, cookies */
 body {
	background: black;
}
#layout-2col #layout-2col-right {
	padding-bottom: 0px;
}
.section-title, .section-subtitle {
	color: #eee;
}
#player-info-name-change {
	font-size: 15px;
}
#currency-maana {
	border: 1px solid;
}
#currency-gold {
	border: 1px solid;
}
/* BACKGROUNDS */
 page-main-container home-interface-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/pet/treasure-hunt/backgrounds/1.jpg);
}
#background-gradient {
	background: black !important;
}
#container .library-main-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/library/library-bg.jpg);
}
#bank-page.page-main-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/bank/background.jpg);
}
.mall-main-container.mall-clothes-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/mall/background_clothes2.jpg);
}
.mall-main-container.mall-pets-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/mall/background_pets.jpg);
}
.mall-main-container.mall-alchemy-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/mall/background_alchemy.jpg);
}
.mall-main-container.mall-clothes2-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/mall/background_clothes.jpg);
}
#layout-2col #layout-2col-right {
	background: rgba(255, 255, 255, 0.2);
}
#layout-2col-left::before {
	background-image: linear-gradient(0, 0), url(https://www.eldarya.es/static/img/home/wave.png);
	opacity: 0.2;
}
#currencies .currency, .button-price-maana, .button-price-legacy, .button-price-gold, .button-price-purropass {
	background-color: #303030;
	line-height: 30px;
}
.pun.button_new {
	margin: 5px 0px;
}
.mCSB_scrollTools .mCSB_draggerContainer {
	background-color: rgba(0, 0, 0, .4);
	width: 4px;
}
.mCSB_scrollTools .mCSB_draggerRail {
	background-color: transparent;
}
#season-switcher::after {
	border-color: transparent transparent #212121 transparent;
}
#season-switcher {
	background: #212121;
}
#player-background, #avatar-background {
	opacity: 0.2;
}
#player-dropdown-menu #player-infos-container {
	background: #212121;
}
#player-dropdown-menu #player-infos-container .player-menu-item {
	color: #01b0c3;
}
#player-dropdown-menu #player-infos-container hr {
	border-bottom: 2px solid rgba(255, 255, 255, 0.2);
}
/* BUTTONS */
 .flavr-button, .nl-button, .marketplace-main-container .page.selected, #bag-container .bag-helper .nl-button, .flavr-container.new-layout-popup .flavr-fixer .flavr-outer .flavr-toolbar .flavr-button.close, .flavr-container.new-layout-popup .flavr-fixer .flavr-outer .flavr-toolbar.inline .flavr-button.close {
	background: linear-gradient(211deg, #01b0c3 0%, #00FFD3 100%);
	border: 2px solid #303030;
	color: #303030;
}
.flavr-button.active, .nl-button.active {
	background: #303030;
}
#marketplace-open-help {
	color: #303030;
}
#marketplace-help-section-close {
	color: #303030;
	width: 27px;
	height: 27px;
}
/* HOME */
 .home-interface-container #right-bg {
	background: rgba(255, 255, 255, 0.2);
}
.home-interface-container #right-bg::before {
	background-image: linear-gradient(0, 0), url(https://www.eldarya.es/static/img/home/wave.png);
	margin: -1px;
	opacity: 0.2;
}
/* PET */
 #pet-infos {
	background: rgba(255, 255, 255, 0.2);
}
#pet-infos .pet-active-days, .pet-interface-container #right-container-inner #title-container p {
	color: #eee;
}
#name-container h3, #name-container h4 {
	color: #ddd;
}
#pet-infos #luck-text {
	margin-top: 0;
	color: #ddd;
}
.pet-interface-container #treasure-hunt-interface .lens {
	border: 1.5px solid rgba(61, 219, 255, .21);
}
#map-location-tooltips .map-location-tooltip .map-location-history {
	background: #212121;
}
#map-location-tooltips .map-location-tooltip .map-location-name, #map-location-tooltips .map-location-tooltip .energy-required span, #map-location-tooltips .map-location-tooltip .time-to-explore span, #map-location-tooltips .map-location-tooltip .map-location-history {
	color: #eee;
}
/* APPEARANCE */
 #appearance-right {
	background: transparent;
}
#appearance-right, .appearance-interface-container #right-bg, .attic-container #right-bg {
	background: rgba(255, 255, 255, 0.1);
	margin-left: -60px;
}
.appearance-interface-container #right-bg::before {
	background-image: linear-gradient(0, 0), url(https://www.eldarya.es/static/img/home/wave.png);
	margin: -1px;
	opacity: 0.2
}
.attic-container #right-bg::before {
	background-image: linear-gradient(0, 0), url(https://www.eldarya.es/static/img/home/wave.png);
	margin: -1px;
	opacity: 0.1;
}
.appearance-category, .appearance-item-icon {
	background: #303030;
}
.appearance-category-name {
	color: #eee;
}
.mCSB_inside > .mCSB_container {
	margin-right: 20px;
}
#appearance-right {
	padding: 60px 30px 0 0;
}
.appearance-items-info {
	height: fit-content;
	background: rgba(255, 255, 255, 0.3);
}
.appearance-items-info div {
	max-height: 40px;
}
.appearance-info-tips {
	color: #c10000;
}
#appearance-items ul {
	min-height: 0px !important;
	max-height: calc(100vh - 520px);
}
.appearance-item.disabled, .appearance-item-group.disabled, .appearance-items-category.disabled .appearance-item, .appearance-items-category.disabled .appearance-item-group {
	background: #a65353 !important;
}
.appearance-item.disabled:hover, .appearance-item-group.disabled:hover, .appearance-items-category.disabled .appearance-item:hover, .appearance-items-category.disabled .appearance-item-group:hover {
	background: #bd7f7f !important;
}
.appearance-item.selected {
	background: #212121 !important;
}
.appearance-item, .appearance-item-group {
	background: #303030 !important;
}
.appearance-category {
	width: 90px;
	height: 115px;
}
.appearance-item-group {
	height: 75px;
	width: 75px;
	text-align: center;
}
.appearance-item-group:hover, .appearance-category:hover {
	background: #606060 !important;
}
.appearance-item-icon, .appearance-item-icon:hover {
	background: transparent;
}
.attic-description {
	color: #eee;
}
/* INVENTORY */
 .inventory-item {
	background: #303030;
}
.inventory-item:hover {
	background: #606060;
}
.inventory-quantity {
	color: #eee;
	font-size: 1.1em;
	background-color: rgba(0, 0, 0, 0.4);
}
#wardrobe-menu li {
	margin: 2px;
	padding: 5px;
	border-radius: 9px;
	width: 30px;
	height: 30px;
}
/*  PURRO'TROC */
 #purrotroc-desc {
	background: rgba(255, 255, 255, 0.5);
}
#minigames-list .section-subtitle {
	color: #eee;
	background: rgba(0, 0, 0, 0.5);
}
/* MALL*/
 #malls-list li h2 {
	color: #ddd;
	background: rgba(0, 0, 0, 0.7);
}
.mall-category, .mall-product, .mall-variation {
	background: #212121;
}
.mall-category:hover, .mall-product:hover, .mall-variation:hover {
	background: #303030;
}
.mall-category::after, .mall-product::after, .mall-variation::after {
	border-color: transparent;
}
.mall-category-title, .mall-product-maana-price, .mall-product-gold-price, .mall-product-legacy-price, .mall-product-purropass-price, .mall-variations-owned {
	color: #3ec1d7;
}
.mall-variation.selected, .mall-product.selected {
	background: #606060;
}
/* MARKET */
 #marketplace-itemDetail {
	display: flex;
}
.marketplace-itemDetail-name {
	display: none;
	/* hidden because bug from eldarya */
}
.marketplace-abstract .abstract-icon {
	background: #303030;
}
.marketplace-main-container h1.section-title {
	margin: 0px;
}
#marketplace-main-content {
	width: 770px;
}
#marketplace-filters {
	margin-top: -10px;
	margin-bottom: 10px;
}
.marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item {
	background-color: #303030;
}
.marketplace-search-item:hover, .marketplace-search-item.selected, .marketplace-search-item:hover .abstract-icon, .marketplace-search-item.selected .abstract-icon, .marketplace-itemsForSale-item:hover, .marketplace-itemsForSale-item.selected, .marketplace-itemsForSale-item:hover .abstract-icon, .marketplace-itemsForSale-item.selected .abstract-icon {
	background: #606060;
}
.marketplace-abstract .abstract-name, .marketplace-abstract .abstract-content, .abstract-currentPrice {
	color: #eee;
	line-height: 15px;
}
.marketplace-abstract .abstract-type {
	color: #ddd;
	line-height: 15px;
}
.marketplace-main-container .page {
	color: #303030;
	line-height: 10px;
}
.truncation {
	color: #00FFD3;
}
.marketplace-main-container .page {
	border: 1px solid #303030;
}
#marketplace-help-section {
	font-size: 15px;
	line-height: 15px;
	max-width: 750px;
	color: #eee;
}
/* BANK 
to-do: pay rules*/
 #bank-page .bank-level-card .bank-level-amount {
	color: #eee;
	background-color: #303030;
}
#bank-page .bank-level-card .crossed-out-amount {
	color: #eee;
	background: #606060;
}
#bank-page .product-panel h2 {
	color: #ddd;
}
/* search */
 #marketplace-filters #filter-itemName, #marketplace-filters select {
	background: #eee;
}
/* POPUP */
 .flavr-container.new-layout-popup .flavr-outer {
	background: none;
}
.flavr-container .flavr-outer .flavr-content .flavr-message h1, .flavr-container.buy-incubator-popup .flavr-outer .flavr-content .flavr-message h1 {
	color: #eee;
}
.flavr-container .flavr-fixer .flavr-outer .flavr-content .flavr-message form textarea {
	background: #606060;
	color: #eee;
}
.flavr-container .flavr-fixer .flavr-outer .flavr-toolbar .flavr-button.close::before, .flavr-container .flavr-fixer .flavr-outer .flavr-toolbar.inline .flavr-button.close::before {
	color: #212121;
}
.maana-button, .legacy-currency-button, .gold-button, .purropass-button {
	color: #212121;
	border: 2px solid #212121;
}
.flavr-container.new-layout-popup .flavr-outer::after {
	animation: popup-skew alternate infinite 0ms linear, show-popup forwards 0ms ease-in-out 0ms;
}
.flavr-container.new-layout-popup .flavr-outer::before {
	animation: popup-skew alternate infinite 0ms linear 0ms, show-popup forwards 0ms ease-in-out 0ms;
}
/* MESSAGING */
 .contact-main-container .friend {
	background-color: rgba(255, 255, 255, 0.3);
}
.player-name-tools {
	background: #212121;
}
.player-name-tools li, .player-name-tools a {
	color: #eee;
}
#messaging-conversation-content-bloc {
	background: #303030;
	color: #eee;
}
.messaging-conversation-author-pseudo {
	color: #3ec1d7;
}
[data-type="0"] #messaging-conversation-content-bloc.group-member {
	background: #212121;
}
[data-type="0"] #messaging-conversation-content-bloc.group-moderator {
	background: #00a3ae;
}
[data-type="0"] #messaging-conversation-content-bloc.group-administrator {
	background: #ffe799;
	color: #4b4e77;
}
#messaging-conversation-author.group-member a {
	background: #212121;
}
#messaging-conversation-author.group-moderator a {
	background: #00a3ae;
}
#messaging-conversation-author.group-administrator a {
	background: #ffe799;
}
.messaging-warning-message, #messageDetails #infoban, #messaging-answer-content-bloc {
	background: #303030;
	color: #eee;
}
#messageDetails #messaging-conversation-content-bloc {
	padding-bottom: 5px;
}
#messaging-answer-content-bloc textarea {
	color: #eee;
	border: 1px solid #303030;
}
#messaging .messaging-conversation-deleteConversation {
	color: #3ec1d7;
}
#messageDetails .messaging-conversation-infoBlock {
	color: #3ec1d7;
}
.messaging-warning-message .colorized {
	color: #3ec1d7;
}
#messaging-conversation-content-bloc.isRead-0 {
	background: #006864;
}
/* RULES ASSOCIATED WITH THE FORUM */

/* MAIN BACKGROUND */
 .page-main-container {
	background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(https://www.eldarya.es/static/img/new-layout/pet/treasure-hunt/backgrounds/1.jpg);
}
/* SECTIONS */
 .pun.box.pun_topic_page h2, .pun.box.pun_index_page h2 {
	background: #303030 none;
	color: #eee;
}
/* HEADER AND FOOTER */
 .pun.header, .pun.footbox, .pun.breadcrumb {
	background-color: #34386f;
	color: #eee;
	border: 1px solid #34386f;
}
.pun.footbox p:last-child a {
	color: #eee;
}
.pun.footbox p:last-child a:hover {
	color: #01b0c3;
}
.pun thead {
	color: #eee;
}
.pun th {
	padding: 5px;
}
/* ROWS AND TITLES */
 #forum h2 {
	display: block;
}
.icon_offset_new td.col1, .icon_offset_new td.col2, .icon_offset_new td.col3, .icon_offset_new td.col4 {
	background-color: #394b4d;
}
.pun_topic_page td.col2.sticked_col, .pun_topic_page td.col3.sticked_col {
	background: #4d394b !important;
}
.pun td {
	border-top: dashed 1px gray;
	color: #333;
	padding: 4px 6px;
	background-color: #303030;
}
.pun#index td.col1, .pun#forum .col1, .pun#index td.col2, .pun#forum .col2, .pun#users .col2, .pun#index td.col3, .pun#forum .col3, .pun#users .col3, .pun#index td.col4, .pun#forum .col4 {
	color: #ddd;
}
.pun#index td.col2, .pun#index td.col3 {
	text-align: center;
}
.pun.pun_topic_page td.altcol {
	background-color: #394b4d;
	color: #ddd;
}
.pun .inbox {
	border: solid 1px #34386F;
	background-color: transparent;
	border-radius: 0px;
}
/* URLs */
 .pun a:hover, .pun.breadcrumb .inline a:hover {
	color: #007f8c;
}
.pun a, .pun.breadcrumb .inline a, #forum .pun.forum_post > h2 > a {
	color: #01b0c3;
}
.pun.breadcrumb li {
	color: #eee;
}
/* BUTTONS */
 .button.button {
	background-color:#01b0c3;
	border: 2px solid #01b0c3;
}
.button.button:hover {
	background-color:#007f8c;
	border: 2px solid #007f8c;
}
/* SEARCH */
 .pun .form {
	color: #eee;
	background: rgb(48, 48, 48);
}
.pun legend, .pun .forminfo, .pun .form {
	color: #eee;
}
/* posts */
 .pun .post {
	border-left: solid 170px rgb(52, 56, 111);
}
.pun .spoil {
	background-color: #acacac;
}
.pun .post, .pun .post_bottom, .pun .inbox {
	background-color: #babcce;
}
.edited em, .post span {
	color: #212121 !important;
}
form#post {
	background-color: #212121;
}
.pun .smileys {
	background-color: #303030;
	width: 210px;
}
.pun .post_area {
	width: 800px;
}
.pun .post_area .longinput, .pun .post_area textarea {
	background: #303030;
	color: white;
}
.pun .msgbox {
	border: solid 1px #34386f;
	background-color: rgba(255, 255, 255, 0.5);
}
.pun_subscribe {
	color: #eee;
}
/* post administrator
 #forum .pun.box > .inbox.group1 .post {
	border-left: solid #916f00 170px;
}
#forum .pun.box > .inbox.group1 div.author_bottom, #forum .pun.box > .inbox.group1 div.author, #forum .pun.box > .inbox.group1 .author .author_title {
	background: #916f00;
}
.pun .inbox.group1 {
	border: solid 1px #916f00;
}
#forum .pun.box > .inbox.group1 .author .author_title strong {
	color:#eee;
} */
 #forum .pun.box > .inbox.group1 .author .author_name .summary {
	color: #4b4e77;
}
.pun .inbox.group1 .author, .pun .inbox.group1 .author_bottom, .pun .inbox.group1 .author_title {
	background: #ffe799;
	color: #4b4e77;
}
/* post moderator */
 #forum .pun.box > .inbox.group2 .post {
	border-left: solid #00a3ae 170px;
}
#forum .pun.box > .inbox.group2 div.author_bottom, #forum .pun.box > .inbox.group2 div.author, #forum .pun.box > .inbox.group2 .author .author_title {
	background: #00a3ae;
}
.pun .inbox.group2 {
	border: solid 1px #00a3ae;
}
#forum .pun.box > .inbox.group2 .author a {
	color: #4b4e77;
}
#forum .pun.box > .inbox.group2 .author .author_title strong {
	color:#eee;
}
/* post normal */
 .pun .author, .pun .author_bottom, .pun .author_title {
	background-color: #34386f;
	color: #ddd;
}
.pun .author_name strong, .pun .author_bottom strong {
	color: white;
}
#forum .pun.box > .inbox .author .author_name .summary, #forum .pun.box > .inbox .author .author_name strong, .pun .spoil > div > h4 {
	color: #eee;
}
/* post sin guardia */
 #forum .pun.box > .inbox.group4 div.author_bottom, #forum .pun.box > .inbox.group4 div.author {
	background: #34386f;
}
#forum .pun.box > .inbox.group4 .post {
	border-left: solid #34386f 170px;
}
.pun .inbox.group4 .author, .pun .inbox.group4 .author_bottom, .pun .inbox.group4 .author_title {
	background: #34386f;
}
.pun .inbox.group4 {
	border-color: #34386f;
}
.pun p:last-child a {
	color: #34386f;
}
/* ACCOUNT */
 #account-personal-bloc, #lost-transactions-left-bloc {
	background: #212121;
}
#account-section .form-row > label, #account-section input[type="text"], #lost-transactions-informations {
	color: #ddd;
}
#account-left::before {
	background-image: linear-gradient(0, 0), url(https://www.eldarya.es/static/img/home/wave.png);
	opacity: 0.2;
}
#account-right {
	background: rgba(255, 255, 255, 0.2);
}
/* EXTRAS */
 .pun td {
	color: #ddd;
}
#moderation .log, #moderation .section {
	background: #212121;
	color: #ddd;
}
#moderation .sub-section {
	background: #303030;
}
#moderation .sub-section label {
	color: #ddd;
}
#signalementActions {
	display: block;
	margin-left: auto;
	margin-right: auto;
	width: 50%;
}
#moderation .options li {
	display: block;
	margin: 25px 0px -5px 0px !important;
	width: 100%;
	height: 100%;
}
/* poll */
 table.pun#sondage td {
	border: none;
	color: #ddd;
	padding: 4px 6px;
}
.pun .poll_bar {
	height: 16px;
	display: inline-block;
	background-color: #007f8c;
}
.poll_bar_text {
	font-weight: bold;
}
.pun.sondage_result {
	text-align: right;
	padding: 10px;
	color: #ddd;
	background-color: #424242;
}
table.pun#sondage {
	border: none;
	margin: 10px 0px 0px 0px;
	width: 100%;
}

.post span.pun_title1 {
	color: white !important;
}
}

@document regexp("https://www.eldarya.*/pet") {
.page-main-container {
	box-shadow: inset 0px 0px 0px 100vmax rgba(0, 0, 0, .7);
}

/* EXPLORATION POINTS WITHOUT EXTRA STYLES */
#map-location-tooltips .map-location-tooltip .tooltip-bg {
	filter: brightness(0.5) !important;
}

#map-location-tooltips .map-location-tooltip .map-location-infos {
	background: #212121;
}
#map-location-tooltips .map-location-tooltip .map-location-name {
	color: #212121;
}
}

@document regexp("https://www.eldarya.*/event/christmas"), regexp("https://www.eldarya.*/pet"), regexp("https://www.eldarya.*/event/christmas/game"), regexp("https://www.eldarya.*/event/christmas/rewards") {
#main-section.event #event-menu.th-game-event, #main-section.event #event-menu.th-game-event, #event-content.th-game-event, .thge-popup.bg2 .flavr-fixer .flavr-outer, .thge-popup.bg0 .flavr-fixer .flavr-outer, .page-main-container.th-game-event {
	filter: brightness(0.8) !important;
}
#event-topbar.th-game-event #server-reward-percentage {
	background-color: #212121;
