css:
@-moz-document domain("github.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.Label--secondary,
.UnderlineNav-item.selected, .UnderlineNav-item[role=tab][aria-selected=true], .UnderlineNav-item[aria-current]:not([aria-current=false]),
.radio-toggle-switch:checked+label,
.render-shell img,
.Box,

:focus,
div.border-wrap,
img.asset
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.css-truncate.css-truncate-overflow,
.css-truncate .css-truncate-overflow,
.css-truncate.css-truncate-target,
.css-truncate .css-truncate-target
{
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: pre-line;
}

.float-lg-right {
    float: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.blob-code-inner,
.f4,
.filter-item,
.filter-list.small .filter-item,
.full-commit .commit-desc pre,
.markdown-body,
.mb-1,

body
{
	font-size: 1.52rem!important;
}

.diffstat,
.file-info,
.mr-3,

relative-time
{
	font-size: 1.2rem!important;
}

.blob-code-inner
{
	line-height: 2.5rem!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

.col-lg-4
{
	margin: 1.2rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.col-10-max,
.container-lg,
.container-xl
{
	max-width: none!important;
}

.text-right
{
	width: auto!important;
}

.col-lg-9 {
    width: 100%!important;
}

}

@-moz-document domain("github.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	
.col-lg-4 {
    width: 31%;
}
	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("github.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.UnderlineNav-item.selected span,
.diffstat,
.Label--secondary,
.filter-item.selected .css-truncate.css-truncate-overflow,
.header-white .Header-old .Header-link:hover, .header-white .Header-old .HeaderMenu-link:hover, .header-white .Header .Header-link:hover, .header-white .Header .HeaderMenu-link:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.color-fg-muted,
.header-white .Header .Header-link,
.header-white .Header .HeaderMenu-link,
.header-white .Header-old .Header-link,
.header-white .Header-old .HeaderMenu-link,
.text-gradient-purple-coral,
.text-gray-mktg,

[for="cycle_monthly"],
[aria-label="Commits on main"],
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.color-fg-default,
.color-fg-subtle,
.f1,
.f6.text-mono.mb-1.border-top.pt-3.mt-3.mb-1,

h1,
h2,
h3.mb-3
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.lh-condensed,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.mr-3,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

relative-time,
time-ago
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.js-computed-value
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.line_through
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.details-overlay>summary,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}


}

@-moz-document domain("github.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}


.file:target {
    box-shadow: 0 0 0 2px #551818 !important;
}

/* Background 
_____________________________________________________________________________________________________*/

.sub-nav-mktg.is-stuck
{
	background: #060505!important;
	background-color: #060505!important;
}

selector
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.MarketplaceBackground-buffer,
.UnderlineNav-item.selected::after, .UnderlineNav-item[role=tab][aria-selected=true]::after, .UnderlineNav-item[aria-current]:not([aria-current=false])::after,
.UnderlineNav-item:hover,
.color-bg-default,
.color-bg-subtle,
.header-white .header-search-wrapper,
.header-white header:not(.header-logged-out) .header-search-wrapper, .header-white header:not(.header-logged-out) .search-input-container,
.jump-to-suggestions-results-container .navigation-focus .jump-to-suggestions-path,
.jump-to-suggestions-results-container [aria-selected=true] .jump-to-suggestions-path,
.radio-toggle-switch:checked+label,
.render-shell img,
.render-shell,

div.border-wrap,
img.asset
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

[data-analytics-event*="Sign up"]
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

[data-analytics-event*="Sign up"]::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

[data-analytics-event*="Sign up"]:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

selector::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

selector:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.btn-large
{	
	background: radial-gradient(circle, #ca3805a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-large::before
{
	background: radial-gradient(circle, #000 0%, #ca3805 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn-large:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.btn-signup-mktg,

[action="/codespaces"] .btn
{	
	background: radial-gradient(circle, #09b705a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-signup-mktg::before,

[action="/codespaces"] .btn::before
{
	background: radial-gradient(circle, #000 0%, #09b705 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.btn-signup-mktg:hover::before,

[action="/codespaces"] .btn:hover::before
{
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

selector
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.dropdown-menu,
.gutter-condensed>[class*=col-],
.jump-to-suggestions-results-container .navigation-focus .jump-to-suggestions-path,
.jump-to-suggestions-results-container [aria-selected=true] .jump-to-suggestions-path
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.dropdown-menu:hover,
.gutter-condensed>[class*=col-]:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.pricing-recommended-plan
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.Box-header,
.file-header,
.header-white .Header-old,
.header-white .Header
{
	background: linear-gradient(to bottom, #161b22 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.Box-row:hover,
.commits-list-item:hover,
.dropdown-item:not(:disabled):hover,
.filter-item.selected,
.filter-item:hover,
.filter-list.small .filter-item:hover,
.issue-list-item:hover,
.menu-item:hover,
.repo-list-item:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.Label--secondary,
.UnderlineNav-item.selected
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}
}

@-moz-document domain("github.com") {
/* Image Settings */

.Box-row,
.UnderlineNav-item,
.col-lg-4,
.col-md-6,
.commits-list-item,
.filter-item,
.issue-list-item,
.menu-item,
.repo-list-item
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.Box-row:hover,
.UnderlineNav-item.selected,
.UnderlineNav-item:hover,
.col-lg-4:hover,
.col-md-6:hover,
.commits-list-item:hover,
.filter-item.selected,
.filter-item:hover,
.issue-list-item:hover,
.menu-item:hover,
.repo-list-item:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}


}

