css:
@-moz-document domain(roblox.com) {
  .dark-theme .btn-common-play-game-lg {
    animation: animate 5s linear infinite;
  }

  .light-theme a.btn-common-play-game-lg {
    animation: animate 5s linear infinite;
  }

  button {
    animation: animate 5s linear infinite;
  }

  @keyframes animate {
    0% {
      filter: hue-rotate(0deg);
    }

    100% {
      filter: hue-rotate(360deg);
    }
  }
}
