/* :: FIREFOX WALLPAPER :: */

@-moz-document url("about:"), url-prefix("about:neterror") {

#errorPageContainer,
#aboutPageContainer
{ 
 margin: 10px auto !important;
 padding: 10px !important;
 border-color: rgb(212,208,200) !important;
}
img { display:none; }

}


@-moz-document url("about:"),url("about:mozilla"),url("about:plugins"),url("about:credits"),url("about:buildconfig"),url("about:cache"), url-prefix("about:cache-entry"), url-prefix("about:cache?device"), url-prefix("about:license"), url-prefix("about:neterror") {

html 
{
margin: 0 !important;
padding: 0 !important;
background: repeat fixed url("data:image/gif;base64,%3D%3D") !important;
}

#moztext { 
 margin-top: 1em !important;
 font-size: 1.5em !important;
}
#from {
 margin: 0 !important;
}

a:visited{ color:#222 !important;}
a:link{ color:navy !important;}
a:hover{ color:red !important;}

body
{
 display: block;
 width: 52em;
 color: black !important;
 background-color: white !important;
 padding: 0.5em 2em 1.5em 2em !important;
 margin: 1em auto !important;
 border: 1px solid ThreeDShadow;
 -moz-border-radius: 10px;
 font-family: arial,sans-serif !important;
 font-size: 75% !important;
}
body:before
{
 display: block;
 margin-top: 5px;
 text-align: center;
 content: url("data:image/png;base64,") !important;
}

}
