/* ==UserStyle==
@name           bookwalker.jp - 8/1/2022, 7:46:14 PM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("bookwalker.jp") {
    body,
    .bw_frame-container,
    .p-top-block .p-main,
    .o-page-section,
    .m-card .m-book-item,
    .m-simple-tile .m-book-item,
    .m-tile .m-book-item {
        background-color: #202124 !important;
    }
    body,
    .p-top-block .p-main .p-main__header .p-main__title {
        color: white !important;
    }

    h1,
    a:active,
    a:hover,
    a:link,
    a:visited {
        color: #6dabe3 !important
    }

    .m-card .m-book-item .m-book-item__info-block .m-book-item__price-box .m-book-item__price .m-book-item__price-tax,
    .m-tile .m-book-item .m-book-item__info-block .m-book-item__price-box .m-book-item__price .m-book-item__price-tax {
        color: white
    }

    .p-top-block .p-main .p-main__header .p-main__title {
        color: black
    }

    .p-action-box .p-action-box__info .p-action-box__info-tax {
        color: white
    }

    .p-card .m-book-item .m-book-item__info-block .m-book-item__summary-text {
        color: white !important
    }
}
