css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("computerbild.de")
{
.page, #posts #superfullsizebanner, #megacontainer, #page_margins, #header, #navigation, #navigation ul, #navigation a, #sitemap, #content_left .linkBlock .linkText {
	width: 100% !important;
}
#content_left .headLine{
	text-align: center !important;
}
body {
	width: 100% !important;
	min-width: 800px;
	height: auto;
	padding: 0 !important;
	margin: 0 !important;
	overflow-x: hidden;
}

#header {
	background-image: url('http://img96.imageshack.us/img96/9242/gradientw.png') !important;
	background-repeat: repeat-x !important;
}
#page {
	padding: 0 !important;
	margin: 0 !important;
}
#main {
	padding-left: 5px !important;
}
#navigation {
	background-color: #ddd  !important;
}
#navigation ul {
	margin-left: 2px !important;
}
#navigation ul:hover {
	margin-left: 0px !important;
}
#navigation #navi_level_1 li {
	width: 12.5%;
	margin: 0 -1 0 -1 !important;
	overflow: hidden;
	background-color: #49b !important;
	background-image: url(http://i.computer-bild.de/i/bg_blue_balken.gif) !important;
	background-repeat: repeat-x !important;
	border-top-left-radius: 25px;
	border-top-right-radius: 25px;
}
#navigation #navi_level_1 li:hover {
	border: 2px solid #279 !important;
	border-bottom: none !important;
	border-top: none !important;
	border-top-left-radius: 15px;
	border-top-right-radius: 15px;
}
#navigation #navi_level_1 li a, #navigation #navi_level_1 li span {
	margin: 0 !important;
	background: transparent !important;
	border-right: 1px solid #ddd !important;
	border-top: 1px solid #ddd !important;
	color: #fff !important;
}
#navigation ul.navi_level_2 {
	margin-left: 0px !important;
}
#navigation:hover ul.navi_level_2 {
	margin-left: 0px !important;
}
#navigation ul.navi_level_2 li a {
	border: none !important;
}
html body 
#navigation ul.navi_level_3{
	width: auto !important;
}
.navi_verlauf {
	width: 100% !important;
	margin-right: 3px !important;
}

#content_left {
	width: auto !important;
	margin-right: 325px !important;
}
#content_left .teaser_cs {
	background-color: #eee !important;
	border: 1px solid #bbb !important;
	border-radius: 15px;
}
#teaser_right {
	position: absolute !important;
	top: 125px !important;
	right: 5px !important;
}



#teaser_right .header, #teaser_right iframe {
	border-top-left-radius: 10px;
	border-top-right-radius: 10px;
}

#teaser_right .content {
	background-color: #eee !important;
}

#content_left .content .box, #content_left .content {
	background: transparent !important;
}

#content_left .fteaser {
	margin-left: 50% !important;
}
#content_left .fteaser .slide, #content_left .fteaser .fpager {
	margin-left: -50% !important;
	width: 475px !important;
}

#content_left .content, #content_left .linkBlock {
	text-align: center;
}
#content_left .content ul, #content_left .content ol {
	text-align: left;
}

#content_left .half {
	background-color: #ddd !important;
	border: 1px solid #bbb!important;
	padding-top: 10px;
}
#content_left .half .content {
	background: transparent !important;
}
#content_left .half .content .leftBlock, #content_left .half .content .rightBlock {
	width: 50%;
	margin: 0 !important;
	padding: 0 !important;
}
#content_left .half .content .leftBlock{
	float: left;
}
#content_left .half .content .rightBlock{
	float: right;
}
#content_left .half .content .textBlock{
	background-color: #ccc !important;
	border: 1px solid #aaa !important;
	margin-left: auto;
	margin-right: auto;
	width: 90%;
	min-width: 215px;
	border-radius: 15px;
}
#content_left .headTopic{
	color: #666;
	width: 100%;
	border-bottom: 1px solid #999;
	margin-bottom: 6px;
}
#content_left .bilderleiste .content{
	margin-left: 50% !important;
	overflow: visible !important;
	background: none;
	border: none;
	margin-bottom: 10px;
}
#content_left .bilderleiste .scroll{
	margin-left: -50% !important;
	padding-right: 5px !important;
	padding-top: 5px !important;
	background-color: #ddd !important;
	border: 1px solid #bbb !important;
	border-radius: 15px;
}
#content_left .bilderleiste a {
	border: 1px solid #ddd !important;
	width: 107px !important;
	height: 81px !important;
	overflow: hidden !important;
	border-radius: 15px;
}

#content_left .additionalLinks {
	margin-left: 50% !important;
}
#content_left .additionalLinks li {
	margin-left: -50% !important;
}

#fvdkoff-target-image{
	display: none;
}

.contentPage {
	background-image: url(http://img.computerbild.de/i/bg_grey.gif) !important;
	border-top: none !important;
	padding-top: 20px !important;
}

#content_left .header .headLine {
	background-image: url(http://i.computer-bild.de/i/bg_blue_balken.gif) !important;
	border: none !important;
	text-align: center !important;
	color: white !important;
	padding-left: 165px;
}
#content_left .header .headLine h2 {
	color: white !important;
}

#content_left .header {
	border: none !important;
}

.imageRight .caption, .imageLeft .caption{
	background: #ddd !important;
	border: 1px solid #bbb !important;
	border-top-color: #eee !important;
	padding: 3px 7px 7px !important;
	border-bottom-left-radius: 15px;
	border-bottom-right-radius: 15px;
}
.imageRight a, .imageLeft a {
	background: #eee !important;
	border: 1px solid #bbb !important;
	border-bottom-color: #eee !important;
}
.imageRight img, .imageLeft img {
	background: #eee !important;
	border: 1px solid #bbb !important;
}
.imageRight a, .imageLeft a {
	border: 0 !important;
}
.imageRight, .imageLeft {
	overflow:visible !important;
}
.imageRight .caption, .imageLeft .caption{
	margin-right: -2px;
}

#content_left .teaser_cs .header, #content_left .header .headLine, #content_left .header .headLineTitle {
	background: none;
	float: none !important;
	margin-left: 0 !important;
}
#content_left .article .area_cs .headLine {
  padding-left: 3px !important;
}

#footer {
	background-color: #aaa;
	border-top: 2px solid #777;
	border-right: 2px solid #777;
	border-top-right-radius: 3em;
	overflow: hidden;
	position: fixed;
	bottom: -5em;
	left: 0;
	right: 315px;
	width: auto;
	color: #ccc !important;
}
#footer *{
	height: 0;
	overflow: hidden !important;
}
#footer:hover *, html body #footer p {
	height: auto !important;
	overflow: auto !important;
}
#footer:hover {
	bottom: 0;
}
#footer a {
	color: #000 !important;
	font-size: 120%;
}
#sitemap {
	margin-left:  -40px !important;
}
#sitemap .header {
	margin-left: 40px !important;
	margin-right: -40px !important;
	border-bottom: 1px solid #ddd !important;
	margin-bottom: 10px !important;
	background-color: #eee !important;
}
#sitemap .content {
	margin-left: 50% !important;
	border: none !important;
}
#sitemap .content .clearfix {
	margin-left: -60% !important;
}
#sitemap .content  .textBlock {
	background-color: #eef !important;
	margin-left: 10px !important;
	border: 1px solid #ccd !important;
	margin-bottom: 25px !important;
	height: 100% !important;
	border-radius: 15px;
}
#sitemap .content  .textBlock .headLine {
	background-color: #dde !important;
}

#footer > div {
	margin: 0 10px 0 10px !important;
}

/*#megacontainer, #superfullsizebanner, #skypescraper {
  position: absolute !important;
  right: -6px !important;
  left: auto !important;
}*/

}
@-moz-document url-prefix(http://www.computerbild.de/artikel/)
{
#google_ads_frame1, #sky {
	position: fixed !important;
	right: 0 !important;
	left: auto !important;
	top: 260px !important;
	max-width: 160px !important;
}
#superfullsizebanner {
	width: 100% !important;
}
#superfullsizebanner object {
	width: 100% !important;
	/*margin-left: auto;
	margin-right: auto;*/
}
#content_left{
	margin-right: 485px !important;
}
#teaser_right{
	right: 165px !important;
	top: 230px !important;
}
#footer, #footer:hover {
	right: 475px;
	bottom: -1.5em !important;
}
}
@-moz-document url-prefix(http://forum.computerbild.de/)
{
#superfullsizebanner iframe {
  margin-left: auto !important;
  margin-right: auto !important;
}
#container {
  width: auto !important;
  margin-right: 118px !important;
}
#skypescraper {
  position: fixed;
  right: -80px;
  left: auto;
  bottom: auto !important;
  top: auto !important;
  margin-top: 0 !important;
}
}
