css:
@-moz-document domain('archiveofourown.org')
{
#main .wrapper {
	background:none;
    border:none;
	box-shadow: none;
}
dl.meta {
    font-family: Verdana, Arial, Helvetica;
    font-size: 0.8em;
    line-height: 1em;
    margin: auto;
    height: 3em;
    max-height: 400px;
    max-width: 100%;
    overflow: auto;
    resize: vertical;
    border-radius: 10px;
}

div.preface .module {
    margin: auto;
    height: 80px;
    max-height: 800px;
    overflow: auto;
    resize: vertical;
    border: solid 1px #aaa;
    border-radius: 10px;
    padding: 10px;
    margin-top: 10px;
    margin-bottom: 10px;
    
}

}
