/* ==UserStyle==
@name         Duolingo: Hide the Hideous Dancing Owl
@description  Hide the hideous dancing owl, infantile animated figures, distracting leader board, friends and other annoying stuff in Duolingo
@version      1.0.0
@author       gambhiro
@license      unlicense
@namespace    https://github.com/openstyles/stylus/
@homepageURL  https://github.com/gambhiro/user-scripts-styles
@updateURL    https://github.com/gambhiro/user-scripts-styles/raw/main/duolingo-hide-the-hideous-dancing-owl.user.css
==/UserStyle== */

@-moz-document domain("duolingo.com") {

/* Hide the hideous dancing owl */
._2EDJJ ._3a8EI._1S4zC, /* lesson loading page */
._1Xlh1 .GMVb4, /* lesson completed page */
._1Xlh1 ._3a8EI._2IFQ2, /* hard exercise */
._1Xlh1 ._3a8EI._3r-Tl /* Perfect! page */
     { display: none !important; }

/* Hide the infantile animated figures */
._1Xlh1 .e4VJZ.FQpeZ[data-test="challenge challenge-translate"] div.F2B9m span._3a8EI._1n2tS._1RL1c
     { display: none !important; }

/* Keep the loading page transition dark. Use transparent instead of
 * rgb(32, 32, 32), so that it works without Darklingo++ as well. */
._3IvEG { background-color: transparent; }

/* Hide DuoPlus icons */
._3ZJK8[data-test=skill-tree] ._1ii1y.np6Tv
     { display: none !important; }

._3W86r ._1_p4S._3Gj5_, /* League leader board */
/* ._3W86r ._2O43A._3ZuGY, /\* XP Progress *\/ */
/* ._3W86r .Yth9H._3ZuGY, /\* Next Achievement *\/ */
._3W86r ._3Gj5_, /* Friends list */
._3W86r ._3Gj5_, /* Follow Duolingo */
._3W86r ._1UOwI._3bfsh, /* Ads on /learn */
._3x0ok ._39CMQ, /* Ads on /skill */
div[aria-hidden=true]
     { display: none !important; }

}
