css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* pageview graph */
div.funsizegraph span.bar {
background:#ffeded url(http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/heartbar5.gif) repeat-x!important;
}

/* search bar */
#searchForm {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 0 -30px !important ;
}

#searchForm div.form {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') no-repeat scroll left top !important ;
}

#searchForm input.itext, #searchForm input.empty:focus {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 0 -30px !important ;
left: 24px !important ;
}

#searchForm input.empty {
background:transparent none repeat scroll 0 0 !important ;
}

#searchForm input.ibutton a {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 100% -60px !important ;
}

#searchForm ul.select li a {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 0 -60px !important ;
}

div.popup2-search a:hover, div popup2-search a:focus {
background:transparent url('http://e.deviantart.com/emoticons/h/heart.gif') no-repeat 3px 4px  !important ;
background-color: #e70156 !important ;
color: #FFFFFF !important ;
padding-left: 23px !important ;
}

div.popup2-search a:active {
background:transparent url('http://e.deviantart.com/emoticons/h/heart.gif') no-repeat 4px 4px  !important ;
background-color: #ffeded !important ;
color: #dc1111 !important ;
}

div.popup2-search a {
background:transparent url('http://e.deviantart.com/emoticons/h/heart.gif') no-repeat 4px 5px  !important ;
background-color: #ffeded !important ;
color: #dc1111 !important ;
padding-left: 23px !important ;
}

#searchForm input.ibutton {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 100% -60px !important;
}

#searchForm input.ibutton:hover, #searchForm input.ibutton:focus {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 100% -90px !important;
}

#searchForm input.ibutton:active {
background:transparent url('http://i234.photobucket.com/albums/ee170/dcz-dacss/pvgraph/vsearch.gif') repeat-x scroll 100% -120px !important;
}
}
