css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
 domain("mangafox.com"),
 domain("forums.mangafox.com") {

/* Remove Ads */
  .clear > .right {
  display: none;
  }


/* Remove Ads (Manga navigation) */

 #top_left_bar {
  background: url("http://s.mfcdn.net/media/search.bottom.left.png") !important;
  width: 7px !important;
  height: 50px !important;
  }

 #top_center_bar {
  background: url("http://s.mfcdn.net/media/search.bottom.center.png") !important;
  width: 736px !important;
  height: 50px !important;
  }

 #top_right_bar {
  background: url("http://s.mfcdn.net/media/search.bottom.right.png") !important;
  width: 7px !important;
  height: 50px !important;
  }

/* Removes Footer */
 #footer {
  display: none;
  }

/* Removes Showcase */
 #showcase {
  display: none;
  }

 div#page > div.left {
  padding-top: 0px !important;
  padding-bottom: 0px !important;
  }

 div#page {
  padding-top: 0px !important;
  }

 div#page > div#content, div#page > div#sidebar {
  padding-top: 10px !important;
  }
}
