css:
@-moz-document domain(lynxcoding.club) {
	html {
      	--edit-bg: /*[[bg]]*/;
     	--scrollbar: /*[[scrollbar]]*/;
      	--gutter: /*[[gutter]]*/;
      	--non-high: /*[[nonHigh]]*/;
      	--funcInput: /*[[funcInput]]*/;
    	--procedure-keyword: /*[[procKey]]*/;
      	--function: /*[[func]]*/;
        --string: /*[[string]]*/;
      	--number: /*[[num]]*/;
      	--com: /*[[com]]*/;
      	--op: /*[[op]]*/;
 	}
	canvas {
    	box-shadow: rgb(0 0 0 / 35%) 0px 5px 15px;
    	border-radius: 0.5em;
	}
  	* {
      	border: 0px;
    }
    html #locDark:before {
    	content: 'Dark Highlights' !important;
	}
  	ul {
      	margin: 0;
    }
  	body.editor {
    	background-color: var(--edit-bg) !important;
	}
  	html.editor.dark ul.list-unstyled.components {
    	background: rgba(0,0,0,0.25);
	}
  	ul.list-unstyled.components {
    	background: rgba(255,255,255,0.25);
	}
  	#sidebar {
    	background: var(--edit-bg);
	}
  	.ace_gutter-cell {
      	color: var(--gutter) !important;
  	}
	#procedures.ace-tomorrow {
    	background: var(--edit-bg) !important;
      	color: var(--non-high) !important;
	}
  	.editor #editor>div:nth-child(2) {
    	background-color: var(--edit-bg) !important;
	}
    ::-webkit-scrollbar {
    	background-color: var(--edit-bg) !important;
	}
  	::-webkit-scrollbar-thumb {
    	background-color: var(--scrollbar) !important;
    	border: 3px solid var(--edit-bg) !important;
	}
 	.fas, .lynx-icon {
      	background-image: url("https://the-river-stole-the-gold.netlify.app/simpleIcons.png") !important;
      	/*just a random site I set up using netlify to essentialy make a private media hosting service*/
    }
  	.fas, .lynx-icon:hover {
      	cursor: pointer;
    }
  	html.editor.dark .ace_gutter-active-line, html.editor.dark .ace_active-line {
      	background: rgba(171,171,171,0.08) !important;
    }
  	.ace_gutter-active-line, .ace_active-line {
      	background: rgba(84,84,84,0.08) !important;
    }
	.ace_keyword {
    	color: var(--procedure-keyword) !important;
	}
  	.ace_identifier {
      	color: var(--non-high) !important;
    }
	.ace_function {
      	color: var(--function) !important;
    }
  	.ace_variable {
    	color: var(--funcInput) !important;
  	}
  	.ace_string {
      	color: var(--string) !important;
    }
  	.ace_numeric {
      	color: var(--number) !important;
    }
  	.ace_comment {
      	color: var(--com) !important;
    }
  .ace_operator {
    	color: var(--op) !important;
  }
  	.ace_line .ace_fold {
      	font: 12px/normal 'Monaco', 'Menlo', 'Ubuntu Mono', 'Consolas', 'source-code-pro', monospace;
      	visibility: hidden;
    }
  	.ace_line .ace_fold:before {
      	visibility: visible;
      	content: "...";
        color: white;
    }
}
