css:
@namespace url(http://www.w3.org/1999/xhtml);

/*plctalk.net dark theme version 1.2 [RH, Gliwice, 6.08.2022] */

@-moz-document domain("plctalk.net") {
body
{
	background: #292929!important;
	color: #cccccc!important;
}
a:link, body_alink
{
	color: #b6c5d8!important;
}
a:visited, body_avisited
{
	color: #b6c5d8!important;
}
a:hover, a:active, body_ahover
{
	color: #ffef96!important;
}
.page
{
	background: #292929!important;
	color: #cccccc!important;
}
.tborder
{
	background: #17191a!important;
	color: #cccccc!important;
	border: 1px solid black!important;
}
.tcat
{
	background: #373538!important;
	color: #cccccc!important;
}
.tcat a:link, .tcat_alink
{
	color: #ccccc!important;
}
.tcat a:visited, .tcat_avisited
{
	color: #ccccc!important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #ffef96!important;
}
.thead
{
	background: #5c5c5b!important;
	color: #cccccc!important;
}
.thead a:link, .thead_alink
{
	color: #cccccc!important;
}
.thead a:visited, .thead_avisited
{
	color: #cccccc!important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
	color: #ffef96!important;
}
.tfoot
{
	background: #17191a!important;
}

.alt1, .alt1Active
{
	background: #484f4f!important;
	color: #cccccc!important;	
}

.alt2, .alt2Active
{
	background: #484f5a!important;
	color: #cccccc!important;
}
.time
{
	color: black!important;
}
.panel
{
	background: #292929!important;
	color: #cccccc!important;
}
.panelsurround
{
	background: #292929!important;
	color: #cccccc!important;
}
legend
{
	color: #cccccc!important;
}
.vbmenu_control
{
	background:  #373538!important;
	color: #cccccc!important;
}
.vbmenu_control a:link, .vbmenu_control_alink
{
	color: #cccccc!important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited
{
	color: #cccccc!important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover
{
	color: #cccccc!important;
}
.vbmenu_popup
{
	background: #5c5c5b!important;
	color: #cccccc!important;
}
.vbmenu_option
{
	background: #5c5c5b!important;
	color: #cccccc!important;
}
.vbmenu_option a:link, .vbmenu_option_alink
{
	color: #cccccc!important;
}
.vbmenu_option a:visited, .vbmenu_option_avisited
{
	color: #cccccc!important;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover
{
	color: #ffef96!important;
} 
}
