css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  url-prefix(http://wordreference.com/),url-prefix(http://comment.news.163.com/),url-prefix(http://www.hao123.com/haoserver/jianfanzh.htm),url-prefix(http://comment.ifeng.com/){
   html, body, tbody, tr, td, div { background-color: #232323 !important; color: #EDBD3E !important }
   pre {
      display: block !important ; 
      background-color: #333333 !important ;
      border: #777777 solid 0.1em !important ;
      color: #999999 !important ;
      font-family: "courier" !important ;
      margin:  1em 2em 2em 1.3em !important ;
      overflow: auto !important ;
      padding: 0.5em 2em 1em 1em !important ;
      text-align: left !important ;
      white-space: pre !important ;
      font-family: "courier" !important ;
   }

   /* defaults for all links */
   a:link, a:visited { color: #999999 !important } 
   a:active { font-weight: bold !important }
   a:hover { color: #007700 !important }
   h1, h2, h3, h4, h5 { color: #007700 !important }

   .tborder { border-width: 0 !important }
   span, i { color: #99AA99 !important }
   sup { color: #007700 !important }
   td.bannertop { display: none !important }
   div.actions,
   div.closeWords,
   p,
   table#searchi,
   table.layout,
   td.tcat,
   td.thead { background-color: #333333 !important ; background-image: none !important }
   tr.even * { background-color: #222222 !important }
   tr.odd * { background-color: #333333 !important }
}
