css:
@-moz-document domain("youtube.com"),
regexp("http(s?)://[^/]*\\.youtube(\\.com)?(\\.[a-z][a-z])?/.*"),
regexp("http(s?)://youtube(\\.com)?(\\.[a-z][a-z])?/.*") 
{

/* Glass-morphism for Youtube search list */
/* by 2023 QuangDD x Kapok Studios */
/* #More about me: quangdd.com */

  #container.ytd-masthead {
    height: 60px;
    padding: 10px 16px 0px 16px;
	}

	#background.ytd-masthead {
    height: 80px;
	}

  #container.ytd-searchbox
  {
    border-radius: 40px;
  }
  
  #search-icon-legacy.ytd-searchbox {
    border-radius: 40px;
		margin: 0 15px;
  }

  #container.ytd-searchbox > [slot=search-input]
  {
    padding-left: 11px;
  } 

  #search-clear-button.ytd-searchbox {
    margin-right: 5px;
  }


	#contentContainer.tp-yt-app-drawer {
    top: 15px;
  }
  
	.gsst_b {
    padding: 0 10px;
  }
  
	.sbdd_a {
    margin-top: 10px;
  }

  .sbdd_b
  {
    background: #ffffff60;
  }
  
  .sbsb_a
  {
    background: rgba( 255, 255, 255, 0.18 );
    box-shadow: 0 8px 32px 0 rgba( 0, 0, 0, 0.37 );
    backdrop-filter: blur(3px);
    -webkit-backdrop-filter: blur(3px);
    border: 1px solid rgba( 255, 255, 255, 0.18 );
  }
  
	.sbsb_d {
		background-color: #ffffff60;
  }

  .sbsb_i
  {
    color: #152a54;
  }

	.sbfl_b {
    background: rgba(255,255,255,0);
  }

	.sbfl_a {
    color: #000;
  }
}
