css:
@-moz-document regexp('https://mattourguide.com/.*') {
  p, span, h1, h2, h3, h4, h5, h6 {
      font-family:"Arial";
  }

  p {
      color:white;   
  }

  #background {

      background-color:#252526 !important;
          color: white !important;
  }

  #background p {
      color: white !important;
  }

  body {
       background-image:none;
      background-color:#1e1e1e !important;
  }

  #scrollCover {
      background: linear-gradient(0deg, #1e1e1e 0%, #1e1e1e 40%, rgba(230,255,253,0) 100%);   
  }

  q-stion > div {
      background-color:#003e72 !important;   
  }

  #TOCBackground {
      color:white;   
  }

  q-stion p {
      color:white !important;
      font-weight:bold;
  }

  #TocBackground {
      background-color:#FF9F1C;
  }

  #breadcrumpContainer {
      color:white;
      font-family:"Arial"
  }

  #subSectionTitle {
      color:white;
      font-family:"Arial";
      font-size:10pt;
  }

  h2 {
      background-color:#333333;
      color:white;
  }

  #contentBackground {
      background:#252526 !important;
      border:none;
  }

  #browserAnbefaling {
      color:white;
      font-family:"Arial";
  }

  #intro {
      background:#1e1e1e;
      padding:2em;
      font-size:10pt;                        
  }
  #introContent {
      font-size:12pt;
      line-height:20px;
  }

  #introMask {
      background: linear-gradient(0deg, #1e1e1e 0%, rgba(230, 255, 253,0) 100%)    
  }

  answer-box  {
      user-select:none;
      cursor:pointer;
  }

  answer-box  p {
      color:black !important;
  }
}
