css:
/* AGENT_SHEET */
@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://guomin.us/),url-prefix(http://twitese.org),url-prefix(http://twitter.com){

/**
 * Page main styles.
 */
body * {
  background-color: transparent !important; }
body {
  background: #1b222b !important;
  color:#D9DB56!important;
}
#content, #side_base, #front #header2 {
  background: #1b222b !important;
  border: 0px solid ;
}
.thumb img {
  border: 0px solid gray !important;
  padding: 0.5px !important;
}
.inner, #topnav {
  font-size:.80em !important; }
.tour {
  border: 0px solid  !important; }
.complete, #security {
  border: 1px solid #666 !important; }
#side .section-header {
  border-bottom: 0px solid #666 !important; }
h1, .teaser, #security {
  color: #ff8 !important; }
p.entry-title {
  font-size: 1.8em !important; }


/**
 * FORMS.
 */
input[type=text], textarea, select {
  background: #666 !important;
  color: #000 !important;
}

/* trying to phase out the "flatbutton" style and just have all buttons default to the same style.
*/
input.submit, button, input[type=submit], input[type=button], input[type="file"] > input[type="button"], #follow-toggle {
  background-color: #222 !important;
  color: #1F242A !important;
  border: 1px solid #444 !important;
}
input.submit:hover, button:hover, input[type=submit]:hover, input[type="file"] > input[type="button"]:hover {
  background: #999 !important; }
input[type="password"] {
  background: #fff !important; }
label {
  color:#fff !important; }


/**
 * Links, timeline-hovers.
 */

/* Hidden links - make visible on keyboard focus.
 */
#accessibility {
  position: static !important;
  overflow: hidden !important;
  width: 0;
  height: 0;
}
#accessibility a:focus {
  position: absolute !important;
  overflow: visible !important;
  top: 0;
  left: 0;
  padding: 2px !important;
  border: 2px solid #777 !important;
}
a {
  color: #9bf !important;
  border: 0px solid transparent;
}

a:hover, a:focus /* a:active - Fix bug in MSIE. */ {
  background: #1b222b !important;
  color: #fff !important;
  text-decoration: underline;
  border: 0px solid #444 !important;
}
#side_base a {
  background-color: #1b222b !important; }
#side_base a:hover, #side_base a:focus {
  background-color: #333 !important; }
.stats a, .promotion a {
  border: none !important; }

#timeline li, #timeline ._status {
  border: 1px solid transparent; }
#timeline li:hover, li:focus {
  background-color: #222 !important;
  border: 0px solid #444;
}
.actions a:hover {
  background-color:transparent !important; }

a *, img {
  background: transparent;
  color: #fff !important;
}


} /*End: @-moz-document */
