css:
@-moz-document url-prefix("https://truckersmp.com")
{
/**
  * TruckersMP Purple Accent (Dark Theme)
  * Created by Digital (https://forum.truckersmp.com/index.php?/profile/4963-digital); edited by Joschi (https://forum.truckersmp.com/index.php?/profile/222408-joschi/)
  */

  .color-green,
.header .navbar-nav > .open > a,
.header .navbar-nav > .open > a:focus,
.header .navbar-nav > .open > a:hover,
.header .navbar-nav > li:hover > a,
.promo-box:hover strong,
.promo-box:hover strong a
  {
    color: #6D41A1;
  }

  .btn-u,
.pricing-head h3,
.tab-v1 .nav-tabs > .active > a,
.tab-v1 .nav-tabs > .active > a:focus,
.tab-v1 .nav-tabs > .active > a:hover,
.tab-v1 .nav-tabs > li > a:hover,
#topcontrol:hover,
.headline-center h2:after
  {
    background: #6D41A1;
  }

.pricing-head h3
  {
    background: #6D41A1;
    text-shadow: 0 1px 0 #6D41A1;
   }
  
  a,
a:hover,
.pricing-content li i,
.breadcrumb li.active,
.breadcrumb li a:hover,
.blog-twitter .blog-twitter-inner a,
.team-v6 small
  {
    color: #EEEEEE;
  }

  .header .navbar-nav > .active > a,
.header .navbar-nav > li > a:hover
  {
    border-bottom: 2px solid #6D41A1;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    border-bottom-color: #6D41A1;
  }

  .header .dropdown-menu
  {
    border-top: 2px solid #6D41A1;
  }

  .headline h2,
.headline h3,
.headline h4
  {
    border-bottom: 2px solid #6D41A1;
  }

  .btn-u.active,
.btn-u:active,
.btn-u:focus,
.btn-u:hover,
.open .dropdown-toggle.btn-u
  {
    background: #56377C;
  }

   .btn-u.btn-u-green.active,
.btn-u.btn-u-green:active,
.btn-u.btn-u-green:focus,
.btn-u.btn-u-green:hover,
.open .dropdown-toggle.btn-u.btn-u-green
  {
    background: #56377C !important;
  }

  .tab-v1 .nav-tabs
  {
    border-bottom: 2px solid #6D41A1;
  }

  body.dark .list-group-item:hover
  {
    background: #333333;
  }

  li.hide-tiny > a > span
  {
    color: #EEEEEE !important;
  }

  .roundcorner
  {
    border: 2px solid #6D41A1;
  }

  .tab-v2 .nav-tabs li.active a
  {
    border-top: 2px solid #6D41A1;
  }

  .panel-u
  {
    border-color: #333;
  }

  .service-block-v8 .service-block-desc h3:after
  {
    background: #6D41A1;
  }
  
  .btn-u.btn-u-green
  {
    background: #6D41A1;
  }
 
  .btn-success
  {
    background-color: #6D41A1;
    border-color: #6D41A1;
  } 

  .btn-success
.btn-success:active,
.btn-success:focus,
.btn-success:hover,
.open .btn-success.dropdown-toggle
  {
    background-color: #56377C;
    border-color: #56377C;
  }

  .btn-success.active,
.btn-success:active,
.open .btn-success.dropdown-toggle
  {
    background-color: #56377C;
    border-color: #56377C;
  }

  .btn-u
  {
    background: #6D41A1;
  }

  .btn-u.btn-u-orange
  {
    background: #6D41A1;
  }

  .btn-u.btn-u-orange.active,
.btn-u.btn-u-orange:active,
.btn-u.btn-u-orange:focus,
.btn-u.btn-u-orange:hover,
.open .dropdown-toggle.btn-u.btn-u-orange
  {
    background: #56377C;
  }
  
  .icon-enabled
  {
    color: #6D41A1 !important;
  }
  
  .fa-tachometer:before
  {
    content: "\F0E4";
    color: #6D41A1 !important;
  }

  .btn-primary
  {
    background-color: #6D41A1 !important;
    border-color: #6D41A1 !important;
  } 
}
