css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* pageview graph */
div.funsizegraph span.bar {
background: #FF0000 url(http://img707.imageshack.us/img707/5125/funsizegraph2.gif)repeat-x!important;
}

/* search bar */
#searchForm {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 0 -30px !important ;
}

#searchForm div.form {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

no-repeat scroll left top !important ;
}

#searchForm input.itext, #searchForm input.empty:focus {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 0 -30px !important ;
left: 24px !important ;
}

#searchForm input.empty {
background:transparent none repeat scroll 0 0 !important ;
}

#searchForm input.ibutton a {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 100% -60px !important ;
}

#searchForm ul.select li a {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 0 -60px !important ;
}

div.popup2-search a:hover, div popup2-search a:focus {
background:transparent none no-repeat 3px 4px  

!important ;
background-color: #222222!important ;
color: #FF0000!important ;
padding-left: 23px !important ;
}

div.popup2-search a:active {
background:transparent none no-repeat 4px 4px  

!important ;
background-color: #000000!important ;
color: #C10202!important ;
}

div.popup2-search a {
background:transparent none 

no-repeat 4px 5px  !important ;
background-color: #000000!important ;
color: #C10202!important ;
padding-left: 23px !important ;
}

#searchForm input.ibutton {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 100% -60px !important;
}

#searchForm input.ibutton:hover, #searchForm input.ibutton:focus {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 100% -90px !important;
}

#searchForm input.ibutton:active {
background:transparent url('http://img16.imageshack.us/img16/3632/customsearchcopy.gif') 

repeat-x scroll 100% -120px !important;
}
}
