css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("videosift.com") {

body, #container, div.uedge, #comment-form, .main_video, .main_video p, .innercontent {
    color: #fff !important;
    background: #000 !important;
}

.innercontent h1 {
    color: #bbb !important;
}

a, .innercontent a, .innercontent h1 a, div.main_video a, #footer a {
    color: #77f !important;
}

input.button {
    color: #77f !important;
    background: #000 !important;
    border: 1px solid #bbb !important;
}

select {
    color: #77f !important;
    background: #000 !important;
    border: thin solid #bbb;
}

#comments textarea, input#search {
    color: #fff !important;
    background: #000 !important;
    border: thin solid #bbb;
}

div.uedge, div.redge, div.bedge, div.ledge, div.ulcorner, div.urcorner,  div.blcorner, div.brcorner, div.brcorner_main {
    background-image: none !important;
}

div.uedge {
    border: 1px solid #bbb !important;
    padding-bottom: 0px !important;
}

.window_top {
    background: #000 !important;
}

#where_tabs {
    background: none !important;
}

#where_tabs li a {
    background: none !important;
}

#where_tabs li.current {
    background: #333 !important;
    background-image: none !important;
}

#header {
    background-image: none !important;
    background: #000 !important;
}

#header_left, #header_right {
    background-image: none !important;
}

#header_channels .arrowbox {
    background-image: none !important;
}

#submenu {
    background-image: none !important;
    background: #333 !important;
    color: #fff !important;
    padding-bottom: 0px !important;
    margin-bottom: 5px !important;
}

#submenu a {
    color: #77f !important;
}

#submenu_left, #submenu_right, #footer {
    display: none !important;
}

.menealo img {
    visibility: hidden !important;
}

.menealo span {
    background-image: url('data:image/png;base64,') !important;
    color: #77f !important;
}

.votes, .vote_cast {
    background-image: none !important;
    background: #000 !important;
}

.votes h1, .votes h2, .vote_text {
    color: #77f !important;
}

.vote_cast h2 {
    color: #bbb !important;
}


}
