css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("flashback.org") {
td.quekldcloxzor {display: none !important}                         /*removes right ad div*/
div.fb_adsys_container_Leaderboard {display: none !important}       /*removes top ad div*/
div.fb_adsys_wrapper_TextAds {display: none !important}             /*removes text ads between posts*/

body
{
	background-color: #FFFFFF !important;
	color: #000000 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 5px 10px 10px 10px !important;
}
*[bgcolor="#d9d1b7"]
{
	background-color: #d6dce2 !important;
}
a:link
{
	color: #000000 !important;
}
a:visited
{
	color: #000000 !important;
}
a:hover, a:active
{
	color: #FF4400 !important;
}
.page
{
	background-color: #FFFFFF !important;
	color: #000000 !important;
}
td, th, p, li
{
	font: 8pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tborder
{
	background-color: #FFFFFF !important;
	color: #000000 !important;
}
.tcat
{
	background-color: #B8C0C7 !important;
	color: #000000 !important;
	font: bold 8pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tcat a:link
{
	color: #000000 !important;
	text-decoration: none !important;
}
.tcat a:visited
{
	color: #000000 !important;
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active
{
	color: #000000 !important;
	text-decoration: underline !important;
}
.thead
{
	background-color: #3F4349 !important;
	color: #FFFFFF !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.thead a:link
{
	color: #FFFFFF !important;
}
.thead a:visited
{
	color: #FFFFFF !important;
}
.thead a:hover, .thead a:active
{
	color: #FF4400 !important;
}
.tfoot
{
	background-color: #3F4349 !important;
	color: #FFFFFF !important;
}
.tfoot a:link
{
	color: #FFFFFF !important;
}
.tfoot a:visited
{
	color: #FFFFFF !important;
}
.tfoot a:hover, .tfoot a:active
{
	color: #FF4400 !important;
}
.alt1, .alt1Active
{
	background-color: #E8E9EA !important;
	color: #000000 !important;
}
.alt2, .alt2Active
{
	background-color: #B8C0C7 !important;
	color: #000000 !important;
}
.wysiwyg
{
	background-color: #F5F5FF !important;
	color: #000000 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
textarea, .bginput
{
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.button
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
select
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
option, optgroup
{
	font-size: 11px !important;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.smallfont
{
	font: 8pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.time
{
	color: #000000 !important;
}
.navbar
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.highlight
{
	color: #FF0000 !important;
	font-weight: bold !important;
}
.fjsel
{
	background-color: #3E5C92 !important;
	color: #E0E0F6 !important;
}
.fjdpth0
{
	background-color: #F7F7F7 !important;
	color: #000000 !important;
}
.panel
{
	background-color: #E8E9EA !important;
	color: #000000 !important;
	padding: 10px !important;
	border: 2px outset !important;
}
.panelsurround
{
	background-color: #FFFFFF !important;
	color: #000000 !important;
}
legend
{
	color: #000000 !important;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.vbmenu_control
{
	background-color: #3F4349 !important;
	color: #FFFFFF !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
.vbmenu_control a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active
{
	color: #FFFFFF !important;
	text-decoration: underline !important;
}
.vbmenu_popup
{
	background-color: #FFFFFF !important;
	color: #000000 !important;
	border: 1px solid #0B198C !important;
}
.vbmenu_option
{
	background-color: #BBC7CE !important;
	color: #000000 !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_option a:link
{
	color: #000000 !important;
	text-decoration: none !important;
}
.vbmenu_option a:visited
{
	color: #000000 !important;
	text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite
{
	background-color: #8A949E !important;
	color: #FFFFFF !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_hilite a:link
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:visited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 8pt !important; font-weight: bold !important; }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 4px !important; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none !important; }
.pagenav td { padding: 2px 4px 2px 4px !important; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px !important; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important; }

/* ***** don't change the following ***** */
form { display: inline !important; }
label { cursor: default !important; }
.normal { font-weight: normal !important; }
.inlineimg { vertical-align: middle !important; }
.bannerbox
{
font-family: Helvetica, Arial, sans-serif !important;
font-weight: bold !important;
	font-size: 8pt !important;
	border-top: white solid 2px !important;
	padding: 0 !important;
align: left !important;
}

.bannerbox a, .bannerbox span
{
	padding: 2px 0 !important;
	text-indent: 2px !important;
	display: block !important;
	width: 100% !important;
}

.bannerbox a, .bannerbox a:visited
{
	color: #000 !important;
	background-color: #EEEEEE !important;
}

.bannerbox a:hover, .bannerbox span
{
	color: #FFF !important;
	background-color: #000000 !important;
}
}
@-moz-document domain(flashback.org) {

}
