css:
@-moz-document domain("youtube.com") {

#watch-promoted-videos-container, 
#pyv-placeholder, 
#watch-channel-brand-div {
	display: none;
}
body, #watch-headline-container, #watch-sidebar .watch-module-body {
background-color: #eaeaea;
}
#masthead-container {
border : 0;
}
a.title:visited {
color: #539 !important;
}
.video-list-item .title {
max-height: none;
}
#watch-headline h1 {
overflow: visible;
}
#watch-sidebar .video-list-item-link {
margin-bottom: 7px;
}
.title+.stat {
float: right;
}
.video-thumb-90 {
border: 0 #aaa solid;
width: 114px !important;
height: 62px !important;
}
.vimg90 {
width: 120px  !important;
height: 90px  !important;
margin-left: -3px !important;
margin-top: -12px !important;
}
}
