css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flashback.org") {

	#site-left div.clear {
		padding:0 !important;
	}
	#site-main {
		overflow: auto !important;
	}
	#site-container {
		overflow-x: hidden!important;
		padding-right: 0px !important;
	}
	#site-right form[name="sok"] {
		cursor:pointer !important;
		position:absolute !important;
		-moz-border-radius-topleft:10px;
		-moz-border-radius-topright:10px;
		left:71% !important;
		color:#111!important;
		width:208px !important;
		background:url(http://i48.tinypic.com/2euppoh.jpg) no-repeat !important;
		padding-left:15px !important;
		top:200px !important;
		height:0px !important;
		overflow:hidden !important;
		padding-top:20px !important;
	}
	#site-right form[name="sok"]:hover {
		cursor:default!important;
		padding-top:30px !important;
		top:120px !important;
		height:70px !important;
	}

	#search-button {
		margin-right:10px !important;
	}
	#site-right form[name="sok"] .georgia {
		font-size:10px !important;
		color:#111 !important;
		padding-left:30px !important;
	}

	#site-right form[name="sok"] .clear, #site-right .break, #top-banner {
		display: none !important;
	}
	#site-right .box-right {
		width:0px !important;
		height:0px !important;
		border:0 !important;
		padding:0 !important;
	}

}
