css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvtorrents.com") {

body{
background:#fff url(http://i.imgur.com/nENvz.png) center 20px no-repeat !important;
margin-top:85px !important;
}

div.container{
margin:0 auto !important;
width:1005px !important;
}

div.container > table{
display:none;
}
div.left{
margin-right:10px !important;
}
div.middle{
position:static !important;
float:left !important;
left:auto !important;
}
div.right div[style="width: 100%;"]{
width:auto !important;
}

div.sidebox{
background:#eee !important;
margin-bottom:10px !important;
-moz-border-radius:0 0 2px 2px !important;
}

div.boxbody{
background:#f2f0f0 !important;
padding:10px !important;
overflow:hidden;
}

div.boxhead h2{
background:#3971B4 !important;
font-size:12px !important;
-moz-border-radius:2px 2px 0 0;
}

div.subbox{
background:#bdbbbb !important;
color:#777 !important;
padding:5px !important;
}
div.subbox a{
font-size:12px !important;
outline:none !important;
}
div.subbox a[style="background-color: rgb(17, 34, 170);"]{
color:#3971B4 !important;
background:transparent !important;
}
div.subbox > img{
display:none;
}
div.lineitem{
background:#E7E7E9 !important;
}
table{
border-collapse:collapse;
}
td{
border:#f0f0f0 1px solid;
}
td[onmouseout]{
background:#E7E7E9 !important;
}

tr.rub{
background:#3971B4 !important;
}
tr.list_even{
background:#e7e7e9 !important;
}
tr.rub td{
padding:3px 5px;
}
tr.list_even td{
padding:5px;
}
tr.torrent_row:hover{
background:#e7e7e9 !important;
}

.tinput, .miscbutton2{
border-color:#888 !important;
}
select, input, textarea, .miscbutton2{
background:#fff !important;
border:#888 1px solid !important;
}
select:focus, input:focus{
border:#333 1px solid !important;
}

input[type="submit"]{
background:#f5f5f5 !important;
}

hr{
background:#ccc !important;
}


div#loadingDiv{
top:50% !important;
left:50% !important;
width:200px !important;
height:auto !important;
margin-left:-100px;
margin-top:-60px;
background:rgba(0,0,0,0.8);
-moz-border-radius:4px;
text-align:center;
padding:40px 0;
}
div#loadingDiv br{
display:none;
}
div#loadingDiv font{
background:none !important;
}


}
