css:
@-moz-document domain(www.ncbi.nlm.nih.gov) 
  {
  body {
    background-color: #212121;
    color: #AAA;
  }

  #mc h1 {
    color: #DDD;
  }

  .usa-accordion-content {
    background-color: #222;
  }

  #mc .links-box {
    border: 1px solid #853f05;
    background-color: #7d5729;
    color: #ebaa75;
  }

  #mc .links-box a {
    color: #ed730e;
  }

  #mc .fig, #mc .table-wrap, #mc .suppmat {
    background-color: #212121;
    border: 1px solid #AAA;
  }

  #mc .caption {
    color: #AAA;
  }

  th, td {
    background-color: #444;
    border: 1px solid #CCC;
  }
  }
