css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("w3.org") {

/*
	alt1_main.css file
	DEFAULT STYLES
*/
body {
	margin: 0 !important;
	padding: 0 !important;
}
img {
	display: block !important;
}
a {
	text-decoration: none !important;
}

.none {
	display: none !important;
}

#introLinks > div {
	position: relative !important;
	background: #eee !important;
	width : 100% !important;
}

/* #leftcont, */
body > p + div.navBlock {
	float : left !important;
	width : 20% !important;
	padding : 1% !important;
}
/* #leftnav, */
body > p + div.navBlock ul {
	border-top: 1px solid !important;
}

/* #centercont, */
.newsBlock {
	line-height: 140% !important;
	padding : 2% !important;
	float : left !important;
	width : 51% !important;
}

/* #rightcont, */
body > .newsBlock + .navBlock {
	float : left !important;
	width : 20% !important;
	margin: 0 !important;
	padding: 1% !important;
	color: #334 !important;
	background: #eee !important;
	border-top: 1px solid #ccc !important;
	border-bottom: 1px solid #999 !important;
}

p {
	margin: 0 0 20px !important;
	padding: 0 !important;
}

form { 
	padding: 0 !important;
	margin: 0 !important;
}
input, textarea, select { 
	padding: 2px !important;
	margin: 0px 0px !important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important; 
	font-size: 11px !important; 
	border: 1px solid #999 !important;
}
/* end alt1_main.css */

/*
	alt2_fontandcolor.css file
	GREEN COLOR SCHEME
*/
html {
	background: #D6D4AA !important;
}
body {
	background: #eee !important;
	font-size: 12px !important;
	font-family: helvetica, arial, verdana, sans-serif !important;
}

a {
	color: #555 !important;
	font-weight: bold !important;
	text-decoration: none !important;
}

/* #header, */
#logo {
	margin: 0 !important;
	padding: 0 !important;
	background: url("data:image/jpeg;base64,")
		no-repeat #A7A93E !important;
	
	width : 100% !important;
	height : 115px !important;
}
#logo {
	margin : 0 auto !important;
}
#logo:before {
	content : 'W3C |' !important;
	color: #eee !important;
	font: bold 2em georgia, times, serif !important;
	margin-left : -75% !important;
}
#logo img {
	display : none !important;
}

/* #header span, */
#slogan {
	font-family: helvetica,arial,sans-serif !important;
	font-style: normal !important;
	font-variant: normal !important;
	font-weight: bold !important;
	font-size: 1em !important;
	line-height: normal !important;
	font-size-adjust: none !important;
	font-stretch: normal !important;
	
	display : block !important;
	position : absolute !important;
	top : 4em !important;
	right : 50% !important;
	width : 380px !important;
	
	background : none !important;
	border : none !important;
	color : #eee !important;
}

/* #centercont h1, */
.newsBlock h1 {
	font-size: 22px !important;
	margin: 5px 0px !important;
	padding: 0 !important;
	font-family: georgia, serif !important;
	color: #333 !important;
}

/* #topnav */
#introLinks > div {
	border: 1px solid #ddd !important;
	border-width : 1px 0 !important;
	background: url("data:image/jpeg;base64,") 
		repeat-y #9E9B4D !important;
	padding : 3px 0 !important;
	color : rgb(150, 150, 75) !important;
}
/* #topnav a, */
#introLinks > div a {
	color: #fff !important;
	background: transparent !important;
	border-right: 1px solid #ddd !important;
	padding: 3px 8px !important;
}
/* #topnav a:hover, */
#introLinks > div a:hover {
	color: #fff !important;
	background: #444 !important;
	border-right: 1px solid #ddd !important;
}
/* NON-EXISTENT CLASS, EMULATION BELOW */
/* #topnav a.firsta, */
#introLinks > div a:first-child {
	border-left: 1px solid #ddd !important;
}

/* #leftnav, */
body > p + div.navBlock ul {
	border-top: 1px solid #ddd !important;
}

/* #leftnav a, */
body > p + div.navBlock ul a {
	text-decoration: none !important;
	border-bottom: 1px solid #ddd !important;
	color: #eee !important;
	background: #777 !important;
	display: block !important;
	margin: 0 !important;
	padding: 2px 20px 2px 20px !important;
	font-weight: bold !important;
}
/* #leftnav a:hover, */
body > p + div.navBlock ul a:hover {
	text-decoration: none !important;
	color: #fff !important;
	background: #888 !important;
}

/* #leftcont, */
body > p + div.navBlock {
	border-bottom: 1px solid #eee !important;
	color: #eee !important;
	background: #555 !important;
}
/* #leftcont ul, */
body > p + div.navBlock ul{
	list-style : none !important;
	padding : 0 !important;
}
#container {
	background: #D6D4AA !important;
}
/* #centercont, */
.newsBlock {
	color: #334 !important;
	background: #EDECDA !important;
	border-top: 1px solid #ddd !important;
}

/* #centercont a, */
.newsBlock a {
	text-decoration: none !important;
	color: #666 !important;
	border-bottom: 1px dotted #666 !important;
}
/* #centercont a:hover, */
.newsBlock a:hover {
	text-decoration: none !important;
	color: #e60 !important;
	border-bottom: 1px dotted #666 !important;
}
/* #centercont span.date, */
.newsBlock span.date {
	border: 1px solid #bbb !important;
	color: #e60 !important;
	background: #eee !important;
	padding: 1px 4px !important;
	position: relative !important;
	top: -4px !important;
	left: -18px !important;
	font-weight: bold !important;
	font-size: 11px !important;
}
/* #centercont span.archive, */
.newsBlock span.archive {
	display: block !important;
	margin: 5px 0 0 !important;
}

/* #centercont p, */
.newsBlock p {
	margin: 10px 0px !important;
}

/* .blok, */
.item {
	border: 1px solid #bbb !important;
	padding: 5px 9px !important;
	background: #fafafa !important;
}

h2 {
	margin: 15px 0px 3px 0px !important;
	padding: 3px 21px !important;
	font-size: 14px !important;
	color: #333 !important;
	background: url("data:image/gif;base64,R0lGODlhEAAQAIAAAP+ZAP///yH5BAEAAAEALAAAAAAQABAAAAIjjI+pq+APHYoUzAqZoboyvETB02SjdJEbuqptaqVy4mr2HRQAOw==")
		no-repeat 2px 4px #D6D4AA !important;
	border: 1px solid #bbb !important;
	width : auto !important;
}

/* #leftnav h2.navhead, */
body > p + div.navBlock ul h2.navhead {
	margin: 0 !important;
	padding: 2px 5px 2px 20px !important;
	font-size: 13px !important;
	color: #eee !important;
	background: #413831 !important;
	border: 0 none !important;
}

/* #centercont strong, */
.newsBlock strong {
	font-weight: bold !important;
}

/* #rightcont, */
body > .newsBlock + .navBlock {
	background: #EDECDA !important;
}
/* #rightcont ul, */
body > .newsBlock + .navBlock ul{
	list-style : none !important;
	padding : 0 !important;
}
/* #rightcont h2, */
body > .newsBlock + .navBlock h2 {
	margin: 0 !important;
	padding: 2px 5px 2px 20px !important;
	font-size: 12px !important;
	color: #eee !important;
	background: #666 !important;
	border: 0 none !important;
}

/* #rightcont a, */
body > .newsBlock + .navBlock a {
	text-decoration: none !important;
	border-bottom: 1px solid #fff !important;
	color: #555 !important;
	background: #D6D4AA !important;
	display: block !important;
	margin: 0 !important;
	padding: 2px 20px 2px 20px !important;
	font-weight: bold !important;
}
/* #rightcont a:hover, */
body > .newsBlock + .navBlock a:hover {
	text-decoration: none !important;
	color: #444 !important;
	background: #dde !important;

}
/* end alt2_fontandcolor.css */

/* hide boring stuff */
.small, .copyright {
	display : none !important;
}

/* hide "home" image */
[alt^="W3C"] {
	display : none !important
}

}
