css:
@-moz-document url-prefix("https://soap2day")
{
:root {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}
  body
  {
    background: #3F3F46 !important;
  }
  
  div.col-lg-6.col-sm-6 > div.myp1
  {
      background: #3F3F46 !important;
  }
  
  
  h3 img {
      visibility: hidden;
  }
  
  h3:before  {
      content: "SOAP2DAY";
      font-weight: 700;
      color: #fff;
      letter-spacing: 12;
  }


  div.panel-body {

  color: #fff;
}
  
  .img:before {
      visibility: hidden;
    }
    
    h4.panel-title a {
        color: #1e1e1e !important;
    }

.panel-info > .panel-heading {
    border-radius: 15px;
}
.panel-info {
    border-radius: 15px;
}

  .panel-info > .panel-heading
  {
    color: #1e1e1e !important;
    font-weight: 700;
  }

  .panel-info > .panel-heading
  {
    background-color: #ffb300 !important;
  }

  .panel-info > .panel-heading
  {
    border-color: #ffb300 !important;
  }

  .panel
  {
    background-color: #00000000 !important;
  }

  .form-control
  {
    background-color: #3F3F46 !important;
    color: #ffffff;
    border: 1px solid #96494900 !important;
    margin-left: 60px !important    ;
  }
  
  .bg-select {
      background-color: #3F3F46 !important;
  }
  
.pagination a {
    background: #3F3F46 !important;
    border-color: #3F3F46 !important;
}

.pagination .active span {
    border-color: #3F3F46 !important;
}

.pagination .disabled span {
    border-color: #3F3F46 !important;
}

li span {
    background: #3F3F46 !important;
}

  /* hides the arrow icon next to the search bar, not necessary really unless for accessibility reasons ig */
  button.btn-info 
  {
   visibility: hidden;
  }
  
  div.hidden-xs 
  {
   visibility: hidden;   
  }
  
  div.loginbar
  {
      visibility: hidden;
      display: none;
  }
  
  
  div.img-group img {
      border-radius: 8px;
      
        
  }

  .thumbnail
  {
    background-color: #3F3F46 !important;
  }

  .alert-info-ex
  {
    color: #1e1e1e !important;
    background-color: #ffb300 !important;
    border-color: #1e1e1e !important;
    text-transform: uppercase;
    font-weight: 600;
  }
  
    .alert-info
  {
    color: #1e1e1e !important;
    background-color: #ffb300 !important;
    border-color: #1e1e1e !important;
  }

  .panel-info
  {
    border-color: #212122 !important;
  }

  .label.label-info
  {
    background-color: #ffb300 !important;
    color: #212122 !important;
  }

  .alert-warning
  {
    color: #fff !important;
    background-color: #3F3F46 !important;
    border-color: #00000000 !important;
  }

  element.style
  {
    /* height: 33px; */
    /* display: inline; */
    background-color: #00000000;
  }

  .navbar-default
  {
    background-color: #ffb300 !important;
    border-radius: 7px;
    border-color: #1e1e1e !important;
    font-size: 15px;
    padding: 2px 2px;
  }

  .navbar-default .navbar-nav > li > a
  {
    color: #1e1e1e !important;
  }

  .navbar-default .navbar-brand
  {
    color: #1e1e1e !important;
  }
  

  
  div.alert.alert-info a {
      color: #1e1e1e !important;
  }
  
  div#btnSwitch {
      background: #ffb300 !important;
      color: #1e1e1e !important;
  }
  
    div.alert a {
      color: #ffb300 !important;
  }
  
  a {
   color: #ffb300 !important;   
  }
 }
