css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^(?!http://crucible.dvlp.2gis.local/).*")
{
html *, html input { line-height: 150% !important }
}

@-moz-document url-prefix(http://www.google.com/search?)
{
#ms * { line-height: 29px !important }
}

@-moz-document url-prefix('http://www.google.com/reader/view/'), url-prefix('https://www.google.com/reader/view/')
{
html *.goog-button-body { line-height: 50% !important; }
}

@-moz-document url-prefix('http://confluence.dvlp.2gis.local/')
{
h1.pagetitle { line-height: 200% !important; }
}

@-moz-document url-prefix('https://mail.google.com/mail/')
{
.T-I { line-height: 27px !important }
}

@-moz-document url-prefix('http://www.artlebedev.ru/kovodstvo/business-lynch/')
{
html div.LynchComment * { line-height: 120% !important; }
}

@-moz-document url-prefix('http://my.safaribooksonline.com/')
{
#readercontent { clear: both !important; }
}
